import React, { useState, useEffect } from "react";
import PollQuestion from "./PollQuestion";
import line2 from "../images/line2.png";
import SideHeader from "./SideHeader";
import { useParams } from "react-router-dom";
import axios from "axios";
import CreateQuizPopup from "./CreateQuizPopup";

function PollQuestionAnalysis() {
  const { quizId } = useParams();

  const [quiz, setQuiz] = useState();

  const [isCreateQuizPopupOpen, setIsCreateQuizPopupOpen] = useState(false);

  useEffect(() => {
    axios
      .get(`http://localhost:4000/getQuizById/${quizId}`)
      .then((response) => {
        const { quiz } = response.data;
        setQuiz(quiz);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [quizId]);

  return (
    <div
      style={{
        display: "flex",
        height: "100vh",
        width: "100vw",
        overflow: "hidden",
      }}
    >
      <div
        style={{
          height: "100vh",
          width: "20vw",
          backgroundColor: "#FFFFFF",
          boxShadow: "0px 4px 4px 0px #00000040",
        }}
      >
        <SideHeader
          option="analytics"
          setIsCreateQuizPopupOpen={setIsCreateQuizPopupOpen}
        />
      </div>

      <div
        style={{
          height: "100vh",
          width: "80vw",
          backgroundColor: "#EDEDED",
        }}
      >
        <div
          style={{
            marginTop: "3vh",
            height: "12vh",
            width: "100%",
            display: "flex",
          }}
        >
          <div>
            <p
              style={{
                color: "#5076FF",
                fontSize: "2.3em",
                fontWeight: "600",
                paddingTop: "4.5vh",
                paddingLeft: "5.2vw",
                paddingBottom: "1vh",
              }}
            >
              {quiz && quiz.name ? `${quiz.name} Question Analysis` : ""}
            </p>
          </div>
          <div
            style={{
              color: "#FF5D01",
              fontSize: "1.1em",
              fontWeight: "600",
              paddingTop: "4vh",
              paddingLeft: "25vw",
              paddingRight: "2vw",
              paddingBottom: "1vh",
            }}
          >
            {quiz ? (
              <div>
                <p>Created on : {quiz.createdAt}</p>
                <p>Impressions : {quiz.impressions}</p>
              </div>
            ) : (
              <></>
            )}
          </div>
        </div>
        <div style={{ height: "80vh", overflow: "auto", marginTop: "5vh" }}>
          <div
            style={{
              width: "60vw",
              marginLeft: "7vw",
              marginRight: "3vw",
            }}
          >
            {quiz &&
              quiz.questionsArray.map((question, index) => (
                <div>
                  <PollQuestion
                    question={question.question}
                    option1={question.peopleAnsweredOption1}
                    option2={question.peopleAnsweredOption2}
                    option3={question.peopleAnsweredOption3}
                    option4={question.peopleAnsweredOption4}
                    questionNumber={++index}
                  />

                  <img
                    style={{ width: "61.5vw", marginLeft: "0.9vw" }}
                    src={line2}
                    alt="line2"
                  />
                </div>
              ))}
          </div>
        </div>
      </div>
      {isCreateQuizPopupOpen && (
        <div
          style={{
            position: "fixed",
            width: "100%",
            height: "100%",
            background: "#000000CF",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <CreateQuizPopup />
        </div>
      )}
    </div>
  );
}

export default PollQuestionAnalysis;
