import React from "react";

function OptionBox({  }) {
  const handleClick = () => {
    const isCorrect = isSelected === (option === correctAnswer);
    onSelect(option, isCorrect);
  };

  return (
    <div
      onClick={handleClick}
      style={{
        border: isSelected ? "4px solid #5076FF" : "",
        backgroundColor: isSelected ? "#F0F0F0" : "", 
        paddingTop: "3.5vh",
        paddingBottom: "3.5vh",
        width: "18vw",
        marginTop: "10vh",
        fontWeight: "600",
        fontSize: "1.6em",
        textAlign: "center",
        cursor: "pointer",
        borderRadius: "10px",
      }}
    >
      {option}
    </div>
  );
}

export default OptionBox;
