import React, { useState, useEffect } from "react";
import SideHeader from "./SideHeader";
import editIcon from "../images/editIcon.png";
import deleteIcon from "../images/deleteIcon.png";
import shareIcon from "../images/shareIcon.png";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import DeleteQuizPopup from "./DeleteQuizPopup";
import axios from "axios";
import CreateQuizPopup from "./CreateQuizPopup";
import AddQuestionsPopup from "./AddQuestionsPopup";

function AnalyticsPage() {
  const navigate = useNavigate();

  const [isCreateQuizPopupOpen, setIsCreateQuizPopupOpen] = useState(false);
  const [isAddQuestionsPopupOpen, setIsAddQuestionsPopupOpen] = useState(false);

  const handleShareBtn = () => {
    toast.success("Link copied to Clipboard");
  };

  const [isDeleteBtnClicked, setIsDeleteBtnClicked] = useState(false);

  const [quizId, setQuizId] = useState(null);

  const handleDeleteBtn = (quizId) => {
    setQuizId(quizId);
    setIsDeleteBtnClicked(true);
  };

  const [quizzes, setQuizzes] = useState([]);

  let quizNumber = 1;

  useEffect(() => {
    axios
      .get("http://localhost:4000/getAllQuizzes")
      .then((response) => {
        const { dateSortedQuizzes } = response.data;
        setQuizzes(dateSortedQuizzes);
      })
      .catch((error) => console.error("Error fetching quizzes:", error));
  }, [quizzes]);

  const handleQuestionWiseAnalysis = (quiz) => {
    if (quiz.type === "Poll Type") {
      navigate(`/pollAnalysis/${quiz._id}`);
    } else {
      navigate(`/qnaAnalysis/${quiz._id}`);
    }
  };

  const handleCloseDeletePopup = () => {
    setIsDeleteBtnClicked(false);
  };

  return (
    <div
      style={{
        display: "flex",
        height: "100vh",
        width: "100vw",
        overflow: "hidden",
      }}
    >
      <div
        style={{
          height: "100vh",
          width: "20vw",
          backgroundColor: "#FFFFFF",
          boxShadow: "0px 4px 4px 0px #00000040",
        }}
      >
        <SideHeader
          option="analytics"
          setIsCreateQuizPopupOpen={setIsCreateQuizPopupOpen}
        />
      </div>
      <div
        style={{
          height: "100vh",
          width: "80vw",
          backgroundColor: "#EDEDED",
        }}
      >
        <div>
          <p
            style={{
              color: "#5076FF",
              fontWeight: "600",
              fontSize: "2.5em",
              textAlign: "center",
              marginTop: "10vh",
            }}
          >
            Quiz Analysis
          </p>

          <div
            style={{ height: "63vh", overflowY: "auto", overflowX: "hidden" }}
          >
            <p
              style={{
                width: "72vw",
                marginLeft: "4vw",
                marginRight: "4vw",
                color: "#FFFFFF",
                backgroundColor: "#5076FF",
                marginTop: "8vh",
                borderRadius: "10px",
                fontSize: "1.1em",
                paddingTop: "2vh",
                paddingBottom: "2vh",
                fontWeight: "600",
              }}
            >
              <span
                style={{
                  marginLeft: "1.5vw",
                  width: "5vw",
                  display: "inline-block",
                  textAlign: "center",
                }}
              >
                S.No
              </span>
              <span
                style={{
                  width: "10vw",
                  display: "inline-block",
                  textAlign: "center",
                }}
              >
                Quiz Name
              </span>
              <span
                style={{
                  width: "14vw",
                  display: "inline-block",
                  textAlign: "center",
                }}
              >
                Created on
              </span>
              <span
                style={{
                  width: "10vw",
                  display: "inline-block",
                  textAlign: "center",
                }}
              >
                Impression
              </span>
            </p>

            {quizzes.map((quiz, index) => (
              <div
                key={index}
                style={{
                  backgroundColor: index % 2 === 0 ? "#EDEDED" : "#B3C4FF",
                  height: "7.5vh",
                  width: "72vw",
                  marginLeft: "4vw",
                  marginRight: "4vw",
                  borderRadius: "10px",
                }}
              >
                <p
                  style={{
                    fontSize: "1.1em",
                    paddingTop: "1vh",
                    paddingBottom: "1.2vh",
                    fontWeight: "600",
                  }}
                >
                  <span
                    style={{
                      marginLeft: "1.5vw",
                      width: "5vw",
                      display: "inline-block",
                      textAlign: "center",
                    }}
                  >
                    {quizNumber++}
                  </span>
                  <span
                    style={{
                      width: "10vw",
                      display: "inline-block",
                      textAlign: "center",
                    }}
                  >
                    {quiz.name}
                  </span>
                  <span
                    style={{
                      width: "14vw",
                      display: "inline-block",
                      textAlign: "center",
                    }}
                  >
                    {quiz.createdAt}
                  </span>
                  <span
                    style={{
                      width: "10vw",
                      display: "inline-block",
                      textAlign: "center",
                    }}
                  >
                    {quiz.impressions}
                  </span>
                  <span
                    style={{
                      marginLeft: "1vw",
                      width: "7vw",
                      display: "inline-block",
                      textAlign: "center",
                    }}
                  >
                    <img src={editIcon} alt="editIcon" />
                    <span style={{ marginLeft: "0.5vw" }}>
                      <img
                        onClick={() => handleDeleteBtn(quiz._id)}
                        style={{ cursor: "pointer" }}
                        src={deleteIcon}
                        alt="editIcon"
                      />
                    </span>
                    <span style={{ marginLeft: "0.2vw" }}>
                      <img
                        onClick={handleShareBtn}
                        style={{ cursor: "pointer" }}
                        src={shareIcon}
                        alt="editIcon"
                      />
                    </span>
                  </span>

                  <span
                    onClick={() => handleQuestionWiseAnalysis(quiz)}
                    style={{
                      width: "14vw",
                      textAlign: "center",
                      display: "inline-block",
                      marginLeft: "1vw",
                      textDecoration: "underline",
                      cursor: "pointer",
                    }}
                  >
                    Question Wise Analysis
                  </span>
                </p>
              </div>
            ))}
          </div>
          <p
            style={{
              textAlign: "right",
              color: "#474444",
              marginRight: "2vw",
              marginTop: "5vh",
              marginBottom: "2vh",
              fontSize: "1.2em",
              fontWeight: "600",
            }}
          >
            {"{more quiz can be added}"}
          </p>
        </div>
      </div>

      {isDeleteBtnClicked && (
        <div
          style={{
            position: "fixed",
            width: "100%",
            height: "100%",
            background: "#000000CF",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <DeleteQuizPopup quizId={quizId} onClose={handleCloseDeletePopup} />
        </div>
      )}

      {isCreateQuizPopupOpen && (
        <div
          style={{
            position: "fixed",
            width: "100%",
            height: "100%",
            background: "#000000CF",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <CreateQuizPopup
            setIsCreateQuizPopupOpen={setIsCreateQuizPopupOpen}
            setIsAddQuestionsPopupOpen={setIsAddQuestionsPopupOpen}
          />
        </div>
      )}

      {isAddQuestionsPopupOpen && (
        <div
          style={{
            position: "fixed",
            width: "100%",
            height: "100%",
            background: "#000000CF",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <AddQuestionsPopup  setIsAddQuestionsPopupOpen={setIsAddQuestionsPopupOpen}
            setIsQuizPublishedPopupOpen={setIsQuizPublishedPopupOpen}/>
        </div>
      )}
       {isQuizPublishedPopupOpen && (
        <div
          style={{
            position: "fixed",
            width: "100%",
            height: "100%",
            background: "#000000CF",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <QuizPublishedPopup  setIsQuizPublishedPopupOpen={setIsQuizPublishedPopupOpen} />
        </div>
      )}
    </div>
  );
}

export default AnalyticsPage;
