import React, { useState } from "react";
import line from "../images/line.png";
import { useNavigate } from "react-router-dom";

function SideHeader({ option, setIsCreateQuizPopupOpen,setEdit }) {
  const [isCreateQuizClicked, setIsCreateQuizClicked] = useState(false);

  const handleCreateQuiz = () => {
    setIsCreateQuizClicked(true);
    setIsCreateQuizPopupOpen(true);
  };

  const navigate = useNavigate();

  const handleLogout = () => {
    navigate("/");
  };

  const handleDashboardClicked = () => {
    setIsCreateQuizClicked(false);
    navigate("/dashboard");
  };

  const handleAnalyticsClicked = () => {
    setIsCreateQuizClicked(false);
    navigate("/analytics");
  };

  return (
    <div style={{ height: "100%", width: "100%" }}>
      <div style={{ textAlign: "center", color: "#474444", fontWeight: "600" }}>
        <p style={{ marginTop: "4vh", fontSize: "2em" }}>QUIZZIE</p>
        <div style={{ fontSize: "1.7em" }}>
          <p
            onClick={handleDashboardClicked}
            style={{
              marginTop: "22vh",
              paddingTop: "1vh",
              paddingBottom: "1vh",
              marginLeft: "1vw",
              marginRight: "1vw",
              borderRadius: "10px",
              boxShadow:
                option === "dashboard" && !isCreateQuizClicked
                  ? "0px 0px 14px 0px #0000001F"
                  : "",
              cursor: "pointer",
            }}
          >
            Dashboard
          </p>
          <p
            onClick={handleAnalyticsClicked}
            style={{
              marginTop: "6vh",
              paddingTop: "1vh",
              paddingBottom: "1vh",
              marginLeft: "1vw",
              marginRight: "1vw",
              borderRadius: "10px",
              boxShadow:
                option === "analytics" && !isCreateQuizClicked
                  ? "0px 0px 14px 0px #0000001F"
                  : "",
              cursor: "pointer",
            }}
          >
            Analytics
          </p>
          <p
            onClick={handleCreateQuiz}
            style={{
              marginTop: "6vh",
              paddingTop: "1vh",
              paddingBottom: "1vh",
              marginLeft: "1vw",
              marginRight: "1vw",
              borderRadius: "10px",
              boxShadow: isCreateQuizClicked
                ? "0px 0px 14px 0px #0000001F"
                : "",
              cursor: "pointer",
            }}
          >
            Create Quiz
          </p>
        </div>
        <p style={{ marginTop: "20vh" }}>
          <img src={line} alt="line" />
        </p>
        <p
          onClick={handleLogout}
          style={{
            marginTop: "3vh",
            fontSize: "1.8em",
            marginBottom: "2vh",
            cursor: "pointer",
          }}
        >
          Logout
        </p>
      </div>
    </div>
  );
}

export default SideHeader;
