import React from "react";

function EachQuestion() {
  return (
    <div>
      <div style={{ backgroundColor: "yellow", height: "8vh", width: "50vw" }}>
        <input
          style={{
            width: "45vw",
            marginLeft:"vw",
            marginTop:"2vh",
            marginRight:"5vw",
            paddingTop: "2vh",
            paddingBottom: "2vh",
            paddingLeft:"1vw",
            border:"none",
            fontWeight:"600",
            fontSize:"1.2em",
            borderRadius:"10px",
            boxShadow: "0px 0px 25px 0px #00000026",
          }}
          type="text"
          placeholder="Poll Question "
        ></input>
      </div>

      <div
        style={{ backgroundColor: "green", height: "10vh", width: "50vw" }}
      ></div>

      <div
        style={{ backgroundColor: "orange", height: "30vh", width: "50vw" }}
      ></div>

      <div
        style={{ backgroundColor: "brown", height: "8vh", width: "50vw" }}
      ></div>
    </div>
  );
}

export default EachQuestion;
