import React, { useState } from "react";
import line from "../images/line.png";
import { useNavigate } from "react-router-dom";

function SideHeader() {
  const navigate = useNavigate();

  const handleLogout = () => {
    navigate("/");
  };

  const [isDashboardClicked, setIsDashboardClicked] = useState(false);
  const [isAnalyticsClicked, setIsAnalyticsClicked] = useState(false);
  const [isCreateQuizClicked, setIsCreateQuizClicked] = useState(false);

  const handleDashboardClicked = () => {
    setIsDashboardClicked(true);
  };

  const handleAnalyticsClicked = () => {
    setIsAnalyticsClicked(true);
  };

  const handleCreateQuizClicked = () => {
    setIsCreateQuizClicked(true);
  };

  return (
    <div style={{ height: "100%", width: "100%" }}>
      <div style={{ textAlign: "center", color: "#474444", fontWeight: "600" }}>
        <p style={{ marginTop: "4vh", fontSize: "2em" }}>QUIZZIE</p>
        <div style={{ fontSize: "1.4em" }}>
          <p
            onClick={handleDashboardClicked}
            style={{
              marginTop: "24vh",
              height: "5vh",
              width: "18vw",
              paddingTop: "1vh",
              marginLeft: "1vw",
              marginRight: "1vw",
              borderRadius: "10px",
              boxShadow: isDashboardClicked ? "0px 0px 14px 0px #0000001F" : "",
            }}
          >
            Dashboard
          </p>
          <p
            onClick={handleAnalyticsClicked}
            style={{
              marginTop: "6vh",
              height: "5vh",
              width: "18vw",
              paddingTop: "1vh",
              marginLeft: "1vw",
              marginRight: "1vw",
              boxShadow: isAnalyticsClicked ? "0px 0px 14px 0px #0000001F" : "",
            }}
          >
            Analytics
          </p>
          <p
            onClick={handleCreateQuizClicked}
            style={{
              marginTop: "6vh",
              height: "5vh",
              width: "18vw",
              paddingTop: "1vh",
              marginLeft: "1vw",
              marginRight: "5vw",
              boxShadow: isCreateQuizClicked
                ? "0px 0px 14px 0px #0000001F"
                : "",
            }}
          >
            Create Quiz
          </p>
        </div>
        <p style={{ marginTop: "23vh" }}>
          <img src={line} alt="line" />
        </p>
        <p
          onClick={handleLogout}
          style={{
            marginTop: "2vh",
            fontSize: "1.6em",
            marginBottom: "2vh",
            cursor: "pointer",
          }}
        >
          Logout
        </p>
      </div>
    </div>
  );
}

export default SideHeader;
