import React from "react";

function AddQuestionsPopup({
  setIsAddQuestionsPopupOpen,
  setIsQuizPublishedPopupOpen,
}) {
  const handleCancelBtn = () => {
    setIsAddQuestionsPopupOpen(false);
  };

  const handleCreateQuizBtn = () => {
    setIsQuizPublishedPopupOpen(true);
    setIsAddQuestionsPopupOpen(false);
  };

  return (
    <div
      style={{
        paddingBottom: "7vh",
        paddingTop: "6vh",
        paddingRight: "2.5vw",
        paddingLeft: "2.5vw",
        marginLeft: "3vw",
        backgroundColor: "white",
        borderRadius: "10px",
        width: "40vw",
      }}
    >
      <div style={{ backgroundColor: "pink", height: "8vh", width: "40vw" }}>
        <p
          style={{
            marginLeft: "2vw",
            fontSize: "1.3em",
            color: "#9F9F9F",
            display: "inline-block", // Ensures the circle is the size of the content
            borderRadius: "50%", // Makes it a circle
            border: "2px solid #9F9F9F", // Border properties
            width: "7vh", // Adjust the width to your preference
            height: "2vh", // Adjust the height to your preference
            textAlign: "center", // Center the content horizontally
            lineHeight: "1.5em", // Center the content vertically
          }}
        >
          1
        </p>
      </div>

      <div
        style={{ backgroundColor: "yellow", height: "8vh", width: "40vw" }}
      ></div>
      <div
        style={{ backgroundColor: "green", height: "10vh", width: "40vw" }}
      ></div>

      <div
        style={{ backgroundColor: "orange", height: "30vh", width: "40vw" }}
      ></div>
      <div
        style={{ backgroundColor: "brown", height: "8vh", width: "40vw" }}
      ></div>
    </div>
  );
}

export default AddQuestionsPopup;
