import React, { useState } from "react";

function Timer({ newQuiz,onQuestionChange }) {
  const [selectedTimer, setSelectedTimer] = useState("");

  const handleTimerOff = () => {
    setSelectedTimer(null);
    const updatedQuiz = {
      ...newQuiz,
      timer: "OFF",
    };
    onQuestionChange
  };

  const handleTimer5sec = () => {
    setSelectedTimer(5);
    const updatedQuiz = {
      ...newQuiz,
      timer: "5 sec",
    };
  };

  const handleTimer10sec = () => {
    setSelectedTimer(10);
    const updatedQuiz = {
      ...newQuiz,
      timer: "10 sec",
    };
  };

  return (
    <div style={{ width: "100%", textAlign: "center", marginTop: "2vh" }}>
      <p style={{ color: "#9F9F9F", fontSize: "1.2em" }}> Timer</p>
      <p>
        <button
          onClick={handleTimerOff}
          style={{
            color: selectedTimer === null ? "#FFFFFF" : "#9F9F9F",
            marginTop: "3vh",
            border: "none",
            width: "6vw",
            borderRadius: "10px",
            boxShadow: "0px 0px 15px 0px #00000026",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            fontSize: "0.9em",
            background: selectedTimer === null ? "#D60000" : "none",
          }}
        >
          OFF
        </button>
      </p>
      <p>
        <button
          onClick={handleTimer5sec}
          style={{
            color: selectedTimer === 5 ? "#FFFFFF" : "#9F9F9F",
            marginTop: "3vh",
            border: "none",
            width: "6vw",
            borderRadius: "10px",
            boxShadow: "0px 0px 15px 0px #00000026",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            fontSize: "0.9em",
            background: selectedTimer === 5 ? "#D60000" : "none",
          }}
        >
          5 sec
        </button>
      </p>
      <p>
        <button
          onClick={handleTimer10sec}
          style={{
            color: selectedTimer === 10 ? "#FFFFFF" : "#9F9F9F",
            marginTop: "3vh",
            border: "none",
            width: "6vw",
            borderRadius: "10px",
            boxShadow: "0px 0px 15px 0px #00000026",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            fontSize: "0.9em",
            background: selectedTimer === 10 ? "#D60000" : "none",
          }}
        >
          10 sec
        </button>
      </p>
    </div>
  );
}

export default Timer;
