import React from "react";

function OptionBox({ key, option }) {
  return (
    <div onClick={handleClick}
      style={{
        backgroundColor: "#F0F0F0",
        borderRadius: "10px",
        paddingTop: "3.5vh",
        paddingBottom: "3.5vh",
        width: "20vw",
        marginTop: "-12vh",
        fontWeight: "600",
        fontSize: "1.6em",
        textAlign: "center",
      }}
    >
      {option}
    </div>
  );
}

export default OptionBox;
