import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import CompletedQuiz from "./CompletedQuiz";

function LiveQuizInterface() {
  const { quizId } = useParams();

  const [quiz, setQuiz] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [quizCompleted, setQuizCompleted] = useState(false);
  const [timer, setTimer] = useState(null);
  const [timerActive, setTimerActive] = useState(false);

  const [correctAnswersCount, setCorrectAnswersCount] = useState(0);
  const [selectedOptionIndex, setSelectedOptionIndex] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:4000/getQuizById/${quizId}`)
      .then((response) => {
        const { quiz } = response.data;
        setQuiz(quiz);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [quizId]);

  const handleNextOrSubmitQna = () => {
    if (quizCompleted) {
      return;
    }

    let peopleAnsweredCorrectly = 0;
    let peopleAnsweredIncorrectly = 0;

    if (currentQuestionIndex < quiz.questionsArray.length - 1) {
      const currentQuestion = quiz.questionsArray[currentQuestionIndex];

      if (selectedOptionIndex !== null) {
        if (currentQuestion.correctAnswer === selectedOptionIndex) {
          if (!timerActive) {
            setCorrectAnswersCount((prevCount) => prevCount + 1);
            peopleAnsweredCorrectly = 1;
          } else {
            peopleAnsweredIncorrectly = 1;
          }
        } else {
          peopleAnsweredIncorrectly = 1;
        }
      } else {
        peopleAnsweredIncorrectly = 1;
      }

      setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
    } else {
      if (selectedOptionIndex !== null) {
        const currentQuestion = quiz.questionsArray[currentQuestionIndex];
        if (currentQuestion.correctAnswer === selectedOptionIndex) {
          if (!timerActive) {
            setCorrectAnswersCount((prevCount) => prevCount + 1);
            peopleAnsweredCorrectly = 1;
          } else {
            peopleAnsweredIncorrectly = 1;
          }
        } else {
          peopleAnsweredIncorrectly = 1;
        }
      } else {
        peopleAnsweredIncorrectly = 1;
      }

      setQuizCompleted(true);
    }
    setSelectedOptionIndex(null);

    axios
      .patch(
        `http://localhost:4000/questionsUpdation/${quiz._id}/${quiz.questionsArray[currentQuestionIndex]._id}`,
        {
          peopleAnsweredCorrectly,
          peopleAnsweredIncorrectly,
        }
      )
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        if (
          error.response &&
          error.response.data &&
          error.response.data.error
        ) {
          console.log(error);
        }
      });
  };

  const handleNextOrSubmitPoll = () => {
    if (quizCompleted) {
      return;
    }
    let peopleAnsweredOption1 = 0;
    let peopleAnsweredOption2 = 0;
    let peopleAnsweredOption3 = 0;
    let peopleAnsweredOption4 = 0;
    if(selectedOptionIndex===0){
      peopleAnsweredOption1=1;
    }
    if(selectedOptionIndex===1){
      peopleAnsweredOption2=1;
    }
    if(selectedOptionIndex===2){
      peopleAnsweredOption3=1;
    }
    if(selectedOptionIndex===3){
      peopleAnsweredOption4=1;
    }
    
  };

  useEffect(() => {
    const timerInterval = setInterval(() => {
      setTimer((prevTimer) => {
        if (prevTimer > 0) {
          return prevTimer - 1;
        } else {
          setTimerActive(true);
          handleNextOrSubmitQna();
          return 0;
        }
      });
    }, 1000);

    return () => {
      clearInterval(timerInterval);
      setTimerActive(false);
    };
    // eslint-disable-next-line
  }, [timer, currentQuestionIndex, quizCompleted]);

  useEffect(() => {
    if (quiz && quiz.questionsArray && quiz.questionsArray.length > 0) {
      setTimer(
        quiz.timer === "5 sec"
          ? 5
          : quiz.timer === "10 sec"
          ? 10
          : quiz.timer === "OFF" || quiz.type === "Poll Type"
          ? Infinity
          : 0
      );
    }
  }, [currentQuestionIndex, quiz]);

  const handleOptionSelect = (index) => {
    setSelectedOptionIndex(index);
  };

  if (quizCompleted) {
    return (
      <CompletedQuiz
        type={quiz.type}
        correctAnswer={correctAnswersCount}
        totalQuestions={quiz.questionsArray.length}
      />
    );
  }

  return (
    <div
      style={{
        height: "100vh",
        width: "100vw",
        backgroundColor: "#041325",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <div
        style={{
          height: "82vh",
          width: "90vw",
          backgroundColor: "#FFFFFF",
          margin: "9vh 5vw",
          borderRadius: "15px",
          boxShadow: "0px 0px 30px 0px #00000026",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        {quiz && quiz.questionsArray && quiz.questionsArray.length > 0 && (
          <div>
            <div
              style={{
                display: "flex",
                marginTop: "5vh",
                justifyContent: "space-between",
              }}
            >
              <p
                style={{
                  color: "#474444",
                  fontWeight: "600",
                  fontSize: "2em",
                  marginLeft: "3vw",
                }}
              >
                0{currentQuestionIndex + 1}/0{quiz.questionsArray.length}
              </p>

              {quiz.timer !== "OFF" && quiz.type !== "Poll Type" && (
                <p
                  style={{
                    color: "#D60000",
                    fontWeight: "600",
                    fontSize: "2em",
                    marginRight: "3vw",
                  }}
                >
                  00:{timer < 10 ? `0${timer}` : timer}s
                </p>
              )}
            </div>

            <div style={{ marginTop: "2vh", textAlign: "center" }}>
              <p
                style={{
                  color: "#474444",
                  fontSize: "2em",
                  fontWeight: "600",
                }}
              >
                {quiz.questionsArray[currentQuestionIndex].question}
              </p>
            </div>

            <div
              style={{
                display: "flex",
                flexWrap: "wrap",
                width: "50vw",
                marginLeft: "20vw",
                marginRight: "20vw",
                justifyContent: "space-between",
                marginTop: "-3vh",
              }}
            >
              {quiz.questionsArray[currentQuestionIndex].optionValues.map(
                (option, index) => (
                  <div
                    onClick={() => handleOptionSelect(index)}
                    style={{
                      border:
                        selectedOptionIndex === index
                          ? "4px solid #5076FF"
                          : "",
                      backgroundColor: "#F0F0F0",
                      height: "15vh",
                      width: "20vw",
                      marginTop: "10vh",
                      fontWeight: "600",
                      fontSize: "1.6em",
                      textAlign: "center",
                      cursor: "pointer",
                      borderRadius: "10px",
                    }}
                  >
                    {quiz.questionsArray[currentQuestionIndex].optionType ===
                      "Text" && (
                      <p style={{ lineHeight: "15vh" }}> {option.value}</p>
                    )}

                    {quiz.questionsArray[currentQuestionIndex].optionType ===
                      "ImageUrl" && (
                      <img
                        style={{
                          width: "20vw",
                          height: "15vh",
                          borderRadius: "10px",
                        }}
                        src={option.imageUrl}
                        alt="optionImage"
                      />
                    )}

                    {quiz.questionsArray[currentQuestionIndex].optionType ===
                      "TextAndImageUrl" && (
                      <div style={{ display: "flex" }}>
                        <p style={{ width: "9vw", lineHeight: "15vh" }}>
                          {option.value}
                        </p>
                        <img
                          style={{
                            height: "12vh",
                            marginTop: "1.5vh",
                            marginBottom: "1.5vh",
                            borderRadius: "10px",
                            width: "10vw",
                            marginRight: "1vw",
                          }}
                          src={option.imageUrl}
                          alt="optionImage"
                        />
                      </div>
                    )}
                  </div>
                )
              )}
            </div>

            <div
              style={{
                marginTop: "2vh",
                textAlign: "center",
              }}
            >
              <button
                onClick={() => {
                  if (quiz.type === "Poll Type") {
                    handleNextOrSubmitPoll();
                  } else {
                    handleNextOrSubmitQna();
                  }
                }}
                style={{
                  color: "#FFFFFF",
                  backgroundColor: "#60B84B",
                  borderRadius: "10px",
                  border: "none",
                  paddingTop: "2vh",
                  paddingBottom: "2vh",
                  width: "20vw",
                  fontSize: "1.3em",
                  fontWeight: "600",
                  marginTop: "3vh",
                  marginBottom: "2vh",
                  cursor: "pointer",
                }}
              >
                {currentQuestionIndex < quiz.questionsArray.length - 1
                  ? "NEXT"
                  : "SUBMIT"}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default LiveQuizInterface;
