// AddQuestionsPopup.js

import React, { useState } from "react";
import plusicon from "../images/plusicon.png";
import EachQuestion from "./EachQuestion";

function AddQuestionsPopup({
  setIsAddQuestionsPopupOpen,
  setIsQuizPublishedPopupOpen,
}) {
  const [questions, setQuestions] = useState([1]);

  const addQuestion = () => {
    if (questions.length < 5) {
      const newQuestionNumber = questions.length + 1;
      setQuestions([...questions, newQuestionNumber]);
    }
  };

  return (
    <div
      style={{
        paddingBottom: "7vh",
        paddingTop: "4vh",
        paddingRight: "2.5vw",
        paddingLeft: "2.5vw",
        marginLeft: "3vw",
        backgroundColor: "white",
        borderRadius: "10px",
        width: "50vw",
      }}
    >
      <div
        style={{
          height: "10vh",
          width: "100%",
          display: "flex",
          marginLeft: "2vw",
        }}
      >
        {questions.map((questionNumber, index) => (
          <div
            key={questionNumber}
            onClick={() => setIsAddQuestionsPopupOpen(questionNumber)}
          >
            <p
              style={{
                marginTop: "1vh",
                fontSize: "1.3em",
                color: "#9F9F9F",
                display: "inline-block",
                borderRadius: "50%",
                boxShadow: "0px 0px 10px 0px #00000026",
                width: "8vh",
                height: "8vh",
                textAlign: "center",
                lineHeight: "8vh",
                fontWeight: "600",
                marginRight: "1.5vw",
                border:
                 questionNumber===index+1
                    ? "2px solid #60B84B"
                    : "none",
              }}
            >
              {questionNumber}
            </p>
            {index === questions.length - 1 && (
              <span>
                <img onClick={addQuestion} src={plusicon} alt="plusicon" />
              </span>
            )}
          </div>
        ))}

        <div style={{ textAlign: "right", flex: "1" }}>
          <p
            style={{
              color: "#9F9F9F",
              fontSize: "1.2em",
              lineHeight: "9vh",
              marginRight: "3vw",
              textAlign: "right",
            }}
          >
            Max 5 questions
          </p>
        </div>
      </div>

      <EachQuestion
        key={questions.length} // Use the length of questions array as a unique key
        questionNumber={questions.length} // Pass questionNumber as id
        setIsAddQuestionsPopupOpen={setIsAddQuestionsPopupOpen}
        setIsQuizPublishedPopupOpen={setIsQuizPublishedPopupOpen}
      />
    </div>
  );
}

export default AddQuestionsPopup;
