import React, { useState } from "react";

function OptionBox({ onSelect, option }) {
  const [isSelected, setIsSelected] = useState(false);

  const handleClick = () => {
    setIsSelected(true);
    onSelect(option);
  };

  return (
    <div
      onClick={handleClick}
      style={{
        border: isSelected ? "#5076FF" : "",
        backgroundColor: "#F0F0F0",
        borderRadius: "10px",
        paddingTop: "3.5vh",
        paddingBottom: "3.5vh",
        width: "20vw",
        marginTop: "-12vh",
        fontWeight: "600",
        fontSize: "1.6em",
        textAlign: "center",
        cursor:""
      }}
    >
      {option}
    </div>
  );
}

export default OptionBox;
