// ... (previous imports and code)

function LiveQuizInterface() {
  // ... (previous state and useEffect)

  const handleNextOrSubmit = () => {
    if (currentQuestionIndex < quiz.questionsArray.length - 1) {
      setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
    } else {
      // Handle submit logic here, you can make an API call or perform any other action
      console.log("Quiz Submitted!");
    }
  };

  return (
    <div
      style={{
        height: "100%",
        width: "100%",
        backgroundColor: "#041325",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <div
        style={{
          height: "82vh",
          width: "90vw",
          backgroundColor: "#FFFFFF",
          marginLeft: "5vw",
          marginRight: "5vw",
          marginTop: "9vh",
          marginBottom: "9vh",
          borderRadius: "15px",
          boxShadow: "0px 0px 30px 0px #00000026",
        }}
      >
        {quiz && quiz.questionsArray && quiz.questionsArray.length > 0 && (
          <div>
            {/* ... (previous code) */}

            <div style={{ marginTop: "6vh", textAlign: "center" }}>
              <button
                onClick={handleNextOrSubmit}
                style={{
                  color: "#FFFFFF",
                  backgroundColor: "#60B84B",
                  borderRadius: "10px",
                  border: "none",
                  paddingTop: "2vh",
                  paddingBottom: "2vh",
                  width: "17vw",
                  fontSize: "1.3em",
                  fontWeight: "600",
                }}
              >
                {currentQuestionIndex < quiz.questionsArray.length - 1
                  ? "NEXT"
                  : "SUBMIT"}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default LiveQuizInterface;
