import React from "react";
import crossicon from "../images/crossicon.png";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import styles from "./QuizPublishedPopup.module.css";

function QuizPublishedPopup({
  setIsQuizPublishedPopupOpen,
  newlyCreatedQuizId,
}) {
  const handleCross = () => {
    setIsQuizPublishedPopupOpen(false);
  };

  const message = `Your link is here : http://localhost:3000/quiz/${newlyCreatedQuizId}`;

  const handleShareBtn = () => {
    const link = `http://localhost:3000/quiz/${newlyCreatedQuizId}`;
    navigator.clipboard.writeText(link).then(() => {
      toast.success("Link copied to Clipboard");
    });
  };

  return (
    <div className={styles.shareBox}>
      <div className={styles.cross}>
        <img
          onClick={handleCross}
          className={styles.crossIcon}
          src={crossicon}
          alt="crossicon"
        />
      </div>

      <div className={styles.text}>
        <p>Congrats your Quiz is </p>
        <p>Published!</p>
      </div>

      <div>
        <input
          style={{
            backgroundColor: "#EDEDED",
            color: "#474444",
            border: "none",
            borderRadius: "10px",
            marginTop: "5vh",
            marginLeft: "2vw",
            marginRight: "2vw",
            height: "6vh",
            width: "36vw",
            paddingLeft: "1.5vw",
          }}
          value={message}
        ></input>
      </div>

      <div style={{ marginTop: "5vh" }}>
        <button
          onClick={handleShareBtn}
          style={{
            backgroundColor: "#60B84B",
            color: "#FFFFFF",
            width: "14vw",
            marginLeft: "13vw",
            marginRight: "13vw",
            border: "none",
            fontWeight: "600",
            paddingTop: "1.5vh",
            paddingBottom: "1.5vh",
            borderRadius: "10px",
            fontSize: "1.1em",
            letterSpacing: "1px",
            cursor: "pointer",
          }}
        >
          Share
        </button>
      </div>
    </div>
  );
}

export default QuizPublishedPopup;
