import React from "react";

function QnAQuestion({
  question,
  correctAnswers,
  incorrectAnswers,
  questionNumber,
}) {
  return (
    <div style={{ height: "22vh", width: "100%", marginTop: "2vh" }}>
      <p style={{ fontWeight: "600", fontSize: "1.3em", letterSpacing: "1px" }}>
        Q.{questionNumber} {question}
      </p>

      <div
        style={{
          display: "flex",
          gap: "4vw",
          marginLeft: "2vw",
          marginTop: "4vh",
        }}
      >
        <div
          style={{
            color: "#474444",
            fontWeight: "600",
            height: "10vh",
            width: "20vw",
            backgroundColor: "white",
            borderRadius: "5px",
            textAlign: "center",
            paddingTop: "1.3vh",
          }}
        >
          <p style={{ fontSize: "1.8em", marginBottom: "0.3vh" }}>
            {correctAnswers + incorrectAnswers}
          </p>
          <p>People Attempted the question</p>
        </div>

        <div
          style={{
            color: "#474444",
            fontWeight: "600",
            height: "10vh",
            width: "20vw",
            backgroundColor: "white",
            borderRadius: "5px",
            textAlign: "center",
            paddingTop: "1.3vh",
          }}
        >
          <p style={{ fontSize: "1.8em", marginBottom: "0.3vh" }}>
            {correctAnswers}
          </p>
          <p>People Answered Correctly</p>
        </div>

        <div
          style={{
            color: "#474444",
            fontWeight: "600",
            height: "10vh",
            width: "20vw",
            backgroundColor: "white",
            borderRadius: "5px",
            textAlign: "center",
            paddingTop: "1.3vh",
          }}
        >
          <p style={{ fontSize: "1.8em", marginBottom: "0.3vh" }}>
            {incorrectAnswers}
          </p>
          <p>People Answered Incorrectly</p>
        </div>
      </div>
    </div>
  );
}

export default QnAQuestion;
