import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import OptionBox from "./OptionBox";

function LiveQuizInterface() {
  const { quizId } = useParams();

  const [quiz, setQuiz] = useState();

  useEffect(() => {
    axios
      .get(`http://localhost:4000/getQuizById/${quizId}`)
      .then((response) => {
        const { quiz } = response.data;
        setQuiz(quiz);
      })
      .catch((error) => {
        console.log(error);
      });
  });

  return (
    <div
      style={{
        height: "100%",
        width: "100%",
        backgroundColor: "#041325",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <div
        style={{
          height: "82vh",
          width: "90vw",
          backgroundColor: "#FFFFFF",
          marginLeft: "5vw",
          marginRight: "5vw",
          marginTop: "9vh",
          marginBottom: "9vh",
          borderRadius: "15px",
          boxShadow: "0px 0px 30px 0px #00000026",
        }}
      >
        {quiz?(  <div><div
          style={{
            display: "flex",
            marginTop: "5vh",
            marginLeft: "4vw",
            gap: "70vw",
          }}
        >
          <p style={{ color: "#474444", fontWeight: "600", fontSize: "2em" }}>
            01/04
          </p>
          <p
            style={{
              color: "#D60000",
              fontWeight: "600",
              fontSize: "2em",
              marginRight: "3vw",
            }}
          >
            00:10s
          </p>
        </div>

        <div style={{ marginTop: "4vh", textAlign: "center" }}>
          <p style={{ color: "#474444", fontSize: "2em", fontWeight: "600" }}>
            Your question text comes here, its a sample text.
          </p>
        </div>

        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            width: "50vw",
            marginLeft: "20vw",
            marginRight: "20vw",
            gap: "10vw",
            marginTop: "20vh",
          }}
        >
          <OptionBox />
          <OptionBox />
          <OptionBox />
          <OptionBox />
        </div>

        <div style={{ marginTop: "6vh", textAlign: "center" }}>
          <button
            style={{
              color: "#FFFFFF",
              backgroundColor: "#60B84B",
              borderRadius: "10px",
              border: "none",
              paddingTop: "2vh",
              paddingBottom: "2vh",
              width: "17vw",
              fontSize: "1.3em",
              fontWeight: "600",
            }}
          >
            NEXT
          </button>
        </div>
     
      </div>)
      :(<></>)}
      
    </div>
    </div>
  );
}

export default LiveQuizInterface;
