import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function LoginDetails() {
  const [loginData, setLoginData] = useState({
    email: "",
    password: "",
  });

  const handleLoginInputChange = (field, value) => {
    setLoginData({ ...loginData, [field]: value });
  };

  const navigate = useNavigate();

  const [error, setError] = useState("");

  const [isLoginClicked, setIsLoginClicked] = useState(false);

  const handleLoginBtn = () => {
    setIsLoginClicked(true);
    axios
      .post("http://localhost:4000/login", loginData)
      .then((response) => {
        const { message } = response.data;
        toast.success(message);
        setIsLoginClicked(false)
      })
      .catch((error) => {
        if (
          error.response &&
          error.response.data &&
          error.response.data.error
        ) {
          setError(error.response.data.error);
        } else {
          setError("An error occurred while log in");
        }
        console.log(error);
      });
  };

  return (
    <div
      style={{
        height: "100%",
        width: "100%",
        marginTop: "10vh",
      }}
    >
      <div style={{ height: "100%", width: "100%", display: "flex" }}>
        <div
          style={{
            height: "100%",
            width: "40%",
            textAlign: "right",
            paddingRight: "5vw",
            paddingLeft: "1vw",
            fontWeight: "600",
            color: "#474444",
            fontSize: "1.3em",
            paddingBottom: "3vw",
          }}
        >
          <p style={{ height: "22%" }}>Email</p>
          <p style={{ height: "22%" }}>Password</p>
        </div>
        <div style={{ height: "100%", width: "60%" }}>
          <form
            method="post"
            style={{ marginTop: "-4vh", paddingBottom: "3vw" }}
          >
            <p>
              <input
                style={{
                  border:
                    isLoginClicked && loginData.email.length === 0
                      ? "1px solid #D60000"
                      : "",
                }}
                className="signup-login-input"
                value={loginData.email}
                onChange={(e) =>
                  handleLoginInputChange("email", e.target.value)
                }
              ></input>
            </p>
            <p>
              <input
                style={{
                  border:
                    isLoginClicked && loginData.password.length === 0
                      ? "1px solid #D60000"
                      : "",
                }}
                className="signup-login-input"
                value={loginData.password}
                onChange={(e) =>
                  handleLoginInputChange("password", e.target.value)
                }
              ></input>
            </p>
          </form>
        </div>
      </div>
      <div
        style={{
          height: "15%",
          width: "100%",
          marginBottom: "2vh",
          marginTop: "-17vh",
        }}
      >
        <button
          onClick={handleLoginBtn}
          style={{
            marginLeft: "35vw",
            marginRight: "35vw",
            backgroundColor: "#A9BCFF",
            height: "90%",
            width: "22%",
            borderRadius: "8px",
            border: "none",
            fontWeight: "600",
            fontSize: "1.3em",
            color: "#474444",
          }}
        >
          Log In
        </button>
      </div>
      <div>
        {error ? (
          <div>
            <p
              style={{
                color: "#D60000",
                fontSize: "1.5rem",
                marginTop: "5vh",
                textAlign: "center",
                marginBottom: "2vh",
              }}
            >
              {error}
            </p>
          </div>
        ) : (
          <></>
        )}
      </div>
    </div>
  );
}

export default LoginDetails;
