import React, { useState } from "react";
import plusicon from "../images/plusicon.png";
import EachQuestion from "./EachQuestion";

function AddQuestionsPopup({
  setIsAddQuestionsPopupOpen,
  setIsQuizPublishedPopupOpen,
}) {
  const [questions, setQuestions] = useState([1]);

  const handleAddQuestion = () => {
    if (questions.length < 5) {
      setQuestions([...questions, questions.length + 1]);
    }
  };

  const handleQuestionClick = (questionNumber) => {
    // Handle click on a specific question
  };

  return (
    <div
      style={{
        paddingBottom: "7vh",
        paddingTop: "4vh",
        paddingRight: "2.5vw",
        paddingLeft: "2.5vw",
        marginLeft: "3vw",
        backgroundColor: "white",
        borderRadius: "10px",
        width: "50vw",
      }}
    >
      <div
        style={{
          height: "10vh",
          width: "50vw",
          display: "flex",
          justifyContent: "space-between",
        }}
      >
        {questions.map((questionNumber) => (
          <p
            key={questionNumber}
            onClick={() => handleQuestionClick(questionNumber)}
            style={{
              marginLeft: "2vw",
              marginTop: "1vh",
              fontSize: "1.3em",
              color: "#9F9F9F",
              display: "inline-block",
              borderRadius: "50%",
              boxShadow: "0px 0px 10px 0px #00000026",
              width: "8vh",
              height: "8vh",
              textAlign: "center",
              lineHeight: "8vh",
              fontWeight: "600",
              marginRight: "1.5vw",
              cursor: "pointer",
            
            }}
          >
            {questionNumber}
          </p>
        ))}
        {questions.length < 5 && (
          <p
            onClick={handleAddQuestion}
            style={{
              marginLeft: "2vw",
              marginTop: "1vh",
              fontSize: "1.3em",
              color: "#60B84B",
              display: "inline-block",
              borderRadius: "50%",
              boxShadow: "0px 0px 10px 0px #60B84B",
              width: "8vh",
              height: "8vh",
              textAlign: "center",
              lineHeight: "8vh",
              fontWeight: "600",
              marginRight: "1.5vw",
              cursor: "pointer",
            }}
          >
            <img src={plusicon} alt="plusicon" />
          </p>
        )}
        <p
          style={{
            color: "#9F9F9F",
            fontSize: "1.2em",
            lineHeight: "9vh",
            marginRight: "0.5vw",
          }}
        >
          Max 5 questions
        </p>
      </div>

      <EachQuestion
        key={questions[questions.length - 1]}
        setIsAddQuestionsPopupOpen={setIsAddQuestionsPopupOpen}
        setIsQuizPublishedPopupOpen={setIsQuizPublishedPopupOpen}
      />
    </div>
  );
}

export default AddQuestionsPopup;
