import React from "react";
import crossicon from "../images/crossicon.png";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function QuizPublishedPopup({
  setIsQuizPublishedPopupOpen,
  newlyCreatedQuizId,
}) {
  const handleCross = () => {
    setIsQuizPublishedPopupOpen(false);
  };

  const message = `Your link is here : http://localhost:3000/quiz/${newlyCreatedQuizId}`;

  const handleShareBtn = () => {
    const link=""
    navigator.clipboard.writeText(link).then(() => {
      toast.success("Link copied to Clipboard");
    });
  };

  return (
    <div
      style={{
        paddingBottom: "8vh",
        paddingTop: "2vh",
        paddingRight: "2.5vw",
        paddingLeft: "2.5vw",
        marginLeft: "3vw",
        backgroundColor: "white",
        borderRadius: "10px",
        width: "40vw",
      }}
    >
      <div style={{ textAlign: "right", marginRight: "-1.5vw" }}>
        <img
          onClick={handleCross}
          style={{ cursor: "pointer" }}
          src={crossicon}
          alt="crossicon"
        />
      </div>

      <div
        style={{
          textAlign: "center",
          color: "#474444",
          fontSize: "2em",
          fontWeight: "600",
          marginTop: "2vh",
        }}
      >
        <p>Congrats your Quiz is </p>
        <p>Published!</p>
      </div>

      <div>
        <input
          style={{
            backgroundColor: "#EDEDED",
            color: "#474444",
            border: "none",
            borderRadius: "10px",
            marginTop: "5vh",
            marginLeft: "2vw",
            marginRight: "2vw",
            height: "6vh",
            width: "36vw",
          }}
          value={message}
        ></input>
      </div>

      <div style={{ marginTop: "5vh" }}>
        <button
          onClick={handleShareBtn}
          style={{
            backgroundColor: "#60B84B",
            color: "#FFFFFF",
            width: "14vw",
            marginLeft: "13vw",
            marginRight: "13vw",
            border: "none",
            fontWeight: "600",
            paddingTop: "1.5vh",
            paddingBottom: "1.5vh",
            borderRadius: "10px",
            fontSize: "1.1em",
            letterSpacing: "1px",
            cursor: "pointer",
          }}
        >
          Share
        </button>
      </div>
    </div>
  );
}

export default QuizPublishedPopup;
