import React, { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import styles from "./DeleteQuizPopup.module.css";

function DeleteQuizPopup({ quizId, onClose }) {
  const token = localStorage.getItem("jwtToken");

  const headers = {
    Authorization: `Bearer ${token}`,
  };

  const [error, setError] = useState("");

  const userId = localStorage.getItem("userId");

  const handleDeleteQuiz = () => {
    axios
      .delete(`http://localhost:4000/quizDeletion/${userId}/${quizId}`, {
        headers,
      })
      .then((response) => {
        onClose();
        toast.success("Quiz Deleted Successfully");
      })
      .catch((error) => {
        if (
          error.response &&
          error.response.data &&
          error.response.data.error
        ) {
          setError(error.response.data.error);
        } else {
          setError("An error occurred while deleting quiz");
        }
        console.log(error);
      });
  };

  const handleCancelBtn = () => {
    onClose();
  };

  return (
    <div className={styles.deleteBox}>
      <div className={styles.text}>
        <p>Are you confirm you</p>
        <p> want to delete ?</p>
      </div>

      <div className={styles.section2}>
        <button
          onClick={handleDeleteQuiz}
          style={{
            color: "#FFFFFF",
            backgroundColor: "#FF4B4B",
            height: "6vh",
            width: "20vw",
            borderRadius: "10px",
          
            fontSize: "1.2em",
          
            border: "none",
            cursor: "pointer",
          }}
        >
          Confirm Delete
        </button>
        <button
          onClick={handleCancelBtn}
          style={{
            height: "6vh",
            width: "20vw",
            borderRadius: "10px",
            color: "#474444",
            fontSize: "1.2em",
            backgroundColor: "white",
            boxShadow: "0px 0px 15px 0px #00000040",
            border: "none",
            cursor: "pointer",
          }}
        >
          Cancel
        </button>
      </div>
      {error && (
        <p
          style={{
            color: "red",
            marginTop: "4vh",
            textAlign: "center",
            fontSize: "1.3em",
          }}
        >
          {error}
        </p>
      )}
    </div>
  );
}

export default DeleteQuizPopup;
