import React, { useState } from "react";
import plusicon from "../images/plusicon.png";
import smallcrossicon from "../images/smallcrossicon.png";
import EachQuestion from "./EachQuestion";

function AddQuestionsPopup({
  setIsAddQuestionsPopupOpen,
  setIsQuizPublishedPopupOpen,
  quizName,
  quizType,
}) {
  const [selectedQuestion, setSelectedQuestion] = useState(1);

  const [questions, setQuestions] = useState([1]);

  const [optionValues, setOptionValues] = useState([]);

  const addQuestion = () => {
    if (questions.length < 5) {
      const newQuestionNumber = questions.length + 1;
      setQuestions([...questions, newQuestionNumber]);
      setSelectedQuestion(newQuestionNumber);
    }
  };

  const handleQuestionClick = (questionNumber) => {
    setSelectedQuestion(questionNumber);
  };

  const handleDeleteQuestion = (questionNumber) => {
    const remainingQuestions = questions.filter((q) => q !== questionNumber);

    const renumberedQuestions = remainingQuestions.map((_, index) => index + 1);

    setQuestions(renumberedQuestions);
  };

  const handleCancelBtn = () => {
    setIsAddQuestionsPopupOpen(false);
  };

  const handleCreateQuizBtn = () => {
    setIsQuizPublishedPopupOpen(true);
    setIsAddQuestionsPopupOpen(false);
  };

  const handleQuestionChange = (questionNumber, updatedQuestion) => {
    setQuestions((prevQuestions) =>
      prevQuestions.map((question) =>
        question.questionNumber === questionNumber
          ? { ...question, ...updatedQuestion }
          : question
      )
    );
  };

  console.log(quizName);
  console.log(quizType);
  console.log(optionValues);

  return (
    <div
      style={{
        paddingBottom: "7vh",
        paddingTop: "4vh",
        paddingRight: "2.5vw",
        paddingLeft: "2.5vw",
        marginLeft: "3vw",
        backgroundColor: "white",
        borderRadius: "10px",
        width: "50vw",
      }}
    >
      <div
        style={{
          height: "10vh",
          width: "100%",
          display: "flex",
          marginLeft: "2vw",
        }}
      >
        {questions.map((questionNumber, index) => (
          <div key={questionNumber} style={{ position: "relative" }}>
            <p
              onClick={() => handleQuestionClick(questionNumber)}
              style={{
                marginTop: "1vh",
                fontSize: "1.3em",
                color: "#9F9F9F",
                display: "inline-block",
                borderRadius: "50%",
                boxShadow: "0px 0px 10px 0px #00000026",
                width: "8vh",
                height: "8vh",
                textAlign: "center",
                lineHeight: "8vh",
                fontWeight: "600",
                marginRight: "1.5vw",
                cursor: "pointer",
                border:
                  selectedQuestion === questionNumber
                    ? "2px solid #60B84B"
                    : "",
              }}
            >
              {index + 1}
              {index !== 0 && (
                <img
                  onClick={() => handleDeleteQuestion(questionNumber)}
                  src={smallcrossicon}
                  alt="smallcrossicon"
                  style={{
                    position: "absolute",
                    marginLeft: "0.5vw",
                    marginTop: "0.5vh",
                    cursor: "pointer",
                  }}
                />
              )}
            </p>
            {index === questions.length - 1 && (
              <span>
                <img
                  style={{ cursor: "pointer" }}
                  onClick={addQuestion}
                  src={plusicon}
                  alt="plusicon"
                />
              </span>
            )}
          </div>
        ))}

        <div style={{ textAlign: "right", flex: "1" }}>
          <p
            style={{
              color: "#9F9F9F",
              fontSize: "1.2em",
              lineHeight: "9vh",
              marginRight: "3vw",
              textAlign: "right",
            }}
          >
            Max 5 questions
          </p>
        </div>
      </div>
      {questions.map((questionNumber) => (
        <div key={questionNumber}>
          <EachQuestion
            questionNumber={questionNumber}
            selected={selectedQuestion === questionNumber}
            setOptionValues={setOptionValues}
          />
        </div>
      ))}

      <div
        style={{
          height: "6.5vh",
          width: "46vw",
          display: "flex",
          justifyContent: "space-between",
          marginTop: "4vh",
          marginLeft: "2vw",
          marginRight: "2vw",
        }}
      >
        <button
          onClick={handleCancelBtn}
          style={{
            color: "#474444",
            boxShadow: "0px 0px 15px 0px #00000040",
            background: "none",
            fontWeight: "600",
            fontSize: "1.2em",
            width: "17vw",
            borderRadius: "10px",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            border: "none",
            cursor: "pointer",
          }}
        >
          Cancel
        </button>
        <button
          onClick={handleCreateQuizBtn}
          style={{
            color: "#FFFFFF",
            backgroundColor: "#60B84B",
            fontWeight: "600",
            fontSize: "1.2em",
            width: "17vw",
            borderRadius: "10px",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            border: "none",
            cursor: "pointer",
          }}
        >
          Create Quiz
        </button>
      </div>
    </div>
  );
}

export default AddQuestionsPopup;
