import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import OptionBox from "./OptionBox";
import CompletedQuiz from "./CompletedQuiz";

function LiveQuizInterface() {
  const { quizId } = useParams();

  const [quiz, setQuiz] = useState();

  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const [selectedOptions, setSelectedOptions] = useState({});

  const [quizCompleted, setQuizCompleted] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get(`http://localhost:4000/getQuizById/${quizId}`)
      .then((response) => {
        const { quiz } = response.data;
        setQuiz(quiz);
      })
      .catch((error) => {
        console.log(error);
      });
  });

  const handleNextOrSubmit = () => {
    if (currentQuestionIndex < quiz.questionsArray.length - 1) {
      setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
    } else {
      setQuizCompleted(true);
    }
  };

  const handleOptionSelect = (option) => {
    setSelectedOptions((prevSelectedOptions) => {
      const updatedSelectedOptions = { ...prevSelectedOptions };
      updatedSelectedOptions[currentQuestionIndex] = option;
      return updatedSelectedOptions;
    });
  };

  if (quizCompleted) {
    return <CompletedQuiz />; 
  }

  return (
    <div
      style={{
        height: "100%",
        width: "100%",
        backgroundColor: "#041325",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <div
        style={{
          height: "82vh",
          width: "90vw",
          backgroundColor: "#FFFFFF",
          marginLeft: "5vw",
          marginRight: "5vw",
          marginTop: "9vh",
          marginBottom: "9vh",
          borderRadius: "15px",
          boxShadow: "0px 0px 30px 0px #00000026",
        }}
      >
        {quiz && quiz.questionsArray && quiz.questionsArray.length > 0 && (
          <div>
            <div
              style={{
                display: "flex",
                marginTop: "5vh",
                marginLeft: "4vw",
                gap: "70vw",
              }}
            >
              <p
                style={{ color: "#474444", fontWeight: "600", fontSize: "2em" }}
              >
                0{currentQuestionIndex + 1}/0{quiz.questionsArray.length}
              </p>
              <p
                style={{
                  color: "#D60000",
                  fontWeight: "600",
                  fontSize: "2em",
                  marginRight: "3vw",
                }}
              >
                {quiz.timer}s
              </p>
            </div>

            <div style={{ marginTop: "4vh", textAlign: "center" }}>
              <p
                style={{ color: "#474444", fontSize: "2em", fontWeight: "600" }}
              >
                {quiz.questionsArray[currentQuestionIndex].question}
              </p>
            </div>

            <div
              style={{
                display: "flex",
                flexWrap: "wrap",
                width: "50vw",
                marginLeft: "20vw",
                marginRight: "20vw",
                justifyContent: "space-between",
                marginTop: "-3vh",
              }}
            >
              {quiz.questionsArray[currentQuestionIndex].optionValues.map(
                (option, index) => (
                  <OptionBox
                    key={index}
                    option={option}
                    onSelect={handleOptionSelect}
                    isSelected={
                      selectedOptions[currentQuestionIndex] === option
                    }
                  />
                )
              )}
            </div>

            <div style={{ marginTop: "6vh", textAlign: "center" }}>
              <button
                onClick={handleNextOrSubmit}
                style={{
                  color: "#FFFFFF",
                  backgroundColor: "#60B84B",
                  borderRadius: "10px",
                  border: "none",
                  paddingTop: "2vh",
                  paddingBottom: "2vh",
                  width: "17vw",
                  fontSize: "1.3em",
                  fontWeight: "600",
                }}
              >
                {currentQuestionIndex < quiz.questionsArray.length - 1
                  ? "NEXT"
                  : "SUBMIT"}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default LiveQuizInterface;
