import React from "react";
import impressionIcon from "../images/impressionIcon.png";

function TrendingQuizBox({ quizName, impressions, createdAt }) {
  return (
    <div
      style={{
        height: "auto",
        width: "90%", // Adjust this percentage as needed
        maxWidth: "300px", // Set a maximum width if needed
        backgroundColor: "white",
        marginLeft: "auto",
        marginRight: "auto",
        marginTop: "2vh", // Adjust margin based on your design
        borderRadius: "5px",
        fontWeight: "600",
        padding: "1em", // Use padding for internal spacing
      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          paddingRight: "0.2vw",
        }}
      >
        <p style={{ fontSize: "1.2em", margin: 0 }}>{quizName}</p>
        <p style={{ color: "#FF5D01", margin: 0 }}>
          {impressions}
          <img
            style={{ height: "1.5em", width: "1.5em", marginLeft: "0.5em" }}
            src={impressionIcon}
            alt="impressionIcon"
          />
        </p>
      </div>

      <p
        style={{
          color: "#60B84B",
          fontSize: "0.8em", // Adjust font size
          marginTop: "1em", // Adjust spacing
        }}
      >
        Created on: {createdAt}
      </p>
    </div>
  );
}

export default TrendingQuizBox;
