import React from "react";
import plusicon from "../images/plusicon.png";

function AddQuestionsPopup({
  setIsAddQuestionsPopupOpen,
  setIsQuizPublishedPopupOpen,
}) {
  const handleCancelBtn = () => {
    setIsAddQuestionsPopupOpen(false);
  };

  const handleCreateQuizBtn = () => {
    setIsQuizPublishedPopupOpen(true);
    setIsAddQuestionsPopupOpen(false);
  };

  return (
    <div
      style={{
        paddingBottom: "7vh",
        paddingTop: "6vh",
        paddingRight: "2.5vw",
        paddingLeft: "2.5vw",
        marginLeft: "3vw",
        backgroundColor: "white",
        borderRadius: "10px",
        width: "50vw",
      }}
    >
      <div
        style={{
          backgroundColor: "pink",
          height: "9vh",
          width: "50vw",
          display: "flex",
          justifyContent: "space-between",
        }}
      >
       <p>
          <p
            style={{
              marginLeft: "2vw",
              marginTop:"1vh",
              fontSize: "1.3em",
              color: "#9F9F9F",
              display: "inline-block",
              borderRadius: "50%",
              boxShadow: "0px 0px 10px 0px #00000026",
              width: "7vh",
              height: "7vh",
              textAlign: "center",
              lineHeight: "7vh",
              fontWeight: "600",
            }}
          >
            1
          </p>
          <span >
            <img src={plusicon} alt="plusicon" />
          </span>
          </p>

        <p
          style={{
            color: "#9F9F9F",
            fontSize: "1.2em",
            lineHeight: "9vh",
            marginRight: "0.5vw",
          }}
        >
          Max 5 questions
        </p>
      </div>

      <div
        style={{ backgroundColor: "yellow", height: "8vh", width: "50vw" }}
      ></div>
      <div
        style={{ backgroundColor: "green", height: "10vh", width: "50vw" }}
      ></div>

      <div
        style={{ backgroundColor: "orange", height: "30vh", width: "50vw" }}
      ></div>
      <div
        style={{ backgroundColor: "brown", height: "8vh", width: "50vw" }}
      ></div>
    </div>
  );
}

export default AddQuestionsPopup;
