import React, { useState } from "react";

function Timer() {
  const [isTimerOff, setIsTimerOff] = useState(false);
  const [isTimer5sec, setIsTimer5sec] = useState(false);
  const [isTimer10sec, setIsTimr10sec] = useState(false);

  const handleTimerOff=()=>{
    setIsTimerOff(true)
  }
  return (
    <div
      style={{
        width: "100%",
        textAlign: "center",
        marginTop: "3.5vh",
      }}
    >
      <p style={{ color: "#9F9F9F", fontSize: "1.2em" }}> Timer</p>
      <p>
        <button 
          style={{
            color: "#9F9F9F",
            marginTop: "3vh",
            border: "none",
            width: "6vw",
            borderRadius: "10px",
            boxShadow: "0px 0px 15px 0px #00000026",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            fontSize: "0.9em",
            background: "none",
          }}
        >
          OFF
        </button>
      </p>
      <p>
        <button
          style={{
            color: "#9F9F9F",
            marginTop: "3vh",
            border: "none",
            width: "6vw",
            borderRadius: "10px",
            boxShadow: "0px 0px 15px 0px #00000026",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            fontSize: "0.9em",
            background: "none",
          }}
        >
          5 sec
        </button>
      </p>
      <p>
        <button
          style={{
            color: "#9F9F9F",
            marginTop: "3vh",
            border: "none",
            width: "6vw",
            borderRadius: "10px",
            boxShadow: "0px 0px 15px 0px #00000026",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            fontSize: "0.9em",
            background: "none",
          }}
        >
          10 sec
        </button>
      </p>
    </div>
  );
}

export default Timer;
