import React, { useState, useEffect } from "react";
import deleteicon from "../images/deleteIcon.png";

function OptionInputsText({
  questionsArray,
  onQuestionChange,
  quizType,
  editableQuiz,
}) {
  const [options, setOptions] = useState([]);
  const [selectedOptionId, setSelectedOptionId] = useState(null);

  useEffect(() => {
    // Set initial options based on editableQuiz when it is not null
    if (editableQuiz) {
      const initialOptions = editableQuiz.questionsArray.find(
        (q) => q.questionNo === questionsArray.questionNo
      )?.optionValues || [];

      setOptions(initialOptions);
    }
  }, [editableQuiz, questionsArray]);

  const handleAddOption = () => {
    setOptions((prevOptions) => {
      if (prevOptions.length < 4) {
        const newOption = {
          id: prevOptions.length + 1,
          value: "",
        };
        const updatedOptions = [...prevOptions, newOption];

        const updatedQuestion = {
          ...questionsArray,
          optionValues: updatedOptions.map((option) => ({
            value: option.value,
            imageUrl: option.imageUrl,
          })),
        };

        onQuestionChange(updatedQuestion);

        return updatedOptions;
      }

      return prevOptions;
    });
  };

  const handleDeleteOption = (id) => {
    setOptions((prevOptions) =>
      prevOptions.filter((option) => option.id !== id)
    );
  };

  const handleInputChange = (id, value) => {
    setOptions((prevOptions) => {
      const updatedOptions = prevOptions.map((option) =>
        option.id === id ? { ...option, value } : option
      );

      const updatedQuestion = {
        ...questionsArray,
        optionValues: updatedOptions.map((option) => ({
          value: option.value,
          imageUrl: option.imageUrl,
        })),
      };

      onQuestionChange(updatedQuestion);

      return updatedOptions;
    });
  };

  const handleRadioChange = (id) => {
    setOptions((prevOptions) =>
      prevOptions.map((option) =>
        option.id === id ? { ...option, type: "textOptionType" } : option
      )
    );
    setSelectedOptionId(id);

    const questionIndex = id - 1;

    onQuestionChange({
      ...questionsArray,
      correctAnswer: questionIndex,
    });
  };

  return (
    <div>
      {options.map((option) => (
        <div key={option.id} style={{ display: "flex", alignItems: "center" }}>
          {quizType === "QnA" && (
            <input
              type="radio"
              name="optionsGroup"
              id={`option${option.id}`}
              onChange={() => handleRadioChange(option.id)}
            />
          )}
          <label htmlFor={`option${option.id}`}>
            <input
              style={{
                width: "15vw",
                paddingTop: "1vh",
                paddingBottom: "1vh",
                boxShadow: "0px 0px 25px 0px #00000026",
                borderRadius: "10px",
                border: "none",
                fontSize: "1.1em",
                paddingLeft: "1vw",
                marginLeft: "1vw",
                marginTop: "3vh",
                backgroundColor:
                  option.id === selectedOptionId ? "#60B84B" : "white",
                color: option.id === selectedOptionId ? "white" : "#9F9F9F",
              }}
              type="text"
              placeholder="Text"
              value={option.value}
              onChange={(e) => handleInputChange(option.id, e.target.value)}
            />
          </label>
          {option.id > 2 && (
            <img
              style={{ marginLeft: "2vw", marginTop: "2vh", cursor: "pointer" }}
              src={deleteicon}
              alt="deleteicon"
              onClick={() => handleDeleteOption(option.id)}
            />
          )}
        </div>
      ))}

      {options.length < 4 && (
        <div>
          <button
            style={{
              color: "#9F9F9F",
              paddingTop: "1vh",
              paddingBottom: "1vh",
              width: "16vw",
              boxShadow: "0px 0px 25px 0px #00000026",
              borderRadius: "10px",
              border: "none",
              background: "none",
              fontSize: "1.2em",
              marginLeft: "2vw",
              marginTop: "5vh",
            }}
            onClick={handleAddOption}
          >
            Add option
          </button>
        </div>
      )}
    </div>
  );
}

export default OptionInputsText;
