import React, { useState } from "react";
import SignUpDetails from "./SignUpDetails";

function SignupLoginPage() {
  const [isSignUpClicked, setIsSignUpClicked] = useState(false);
  const [isLoginClicked, setIsLoginClicked] = useState(false);

  const handleSignUpButton = () => {
    setIsSignUpClicked(true);
    setIsLoginClicked(false);
  };

  const handleLoginButton = () => {
    setIsLoginClicked(true);
    setIsSignUpClicked(false);
  };

  return (
    <div
      style={{
        height: "100vh",
        width: "100vw",
        backgroundColor: "#F2F2F2",
        display: "flex",
      }}
    >
      <div
        style={{
          backgroundColor: "#FFFFFF",
          height: "78vh",
          width: "86vw",
          marginLeft: "7vw",
          marginRight: "7vw",
          marginTop: "10vh",
          marginBottom: "12vh",
          borderRadius: "15px",
          boxShadow: "0px 0px 30px 0px #00000026",
        }}
      >
        <div
          style={{
            height: "10%",
            width: "100%",
            marginTop: "6vh",
          }}
        >
          <p
            style={{
              fontSize: "5.5vh",
              fontWeight: "600",
              textAlign: "center",
              color: "#474444",
            }}
          >
            QUIZZIE
          </p>
        </div>

        <div
          style={{
            height: "11%",
            width: "100%",
            display: "flex",
            marginTop: "2.7vh",
          }}
        >
          <button
            onClick={handleSignUpButton}
            style={{
              height: "60%",
              width: "13%",
              background: "none",
              border: "none",
              marginTop: "2vh",
              marginLeft: "28vw",
              color: "#474444",
              fontWeight: "bold",
              fontSize: "3vh",
              boxShadow: isSignUpClicked ? "0px 0px 50px 0px #0019FF3D" : "",
            }}
          >
            Sign Up
          </button>
          <button
            onClick={handleLoginButton}
            style={{
              height: "60%",
              width: "13%",
              background: "none",
              border: "none",
              marginTop: "2vh",
              marginLeft: "8vw",
              color: "#474444",
              fontWeight: "bold",
              fontSize: "3vh",
              boxShadow: isLoginClicked ? "0px 0px 50px 0px #0019FF3D" : "",
            }}
          >
            Log In
          </button>
        </div>

        <div
          style={{
            height: "50%",
            width: "100%",
            marginTop: "4vh",
          }}
        >
          <SignUpDetails />
        </div>

       
        <div
        style={{
          height: "8%",
          width: "100%",
          marginTop: "1.5vh",
          marginBottom: "2vh",
        }}
      >
        <button style={{marginLeft:"25vw",backgroundColor:"#A9BCFF",height:"100%",width:"25%"}}>Sign-Up</button>
      </div>
      </div>
    </div>
  );
}

export default SignupLoginPage;
