import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function LiveQuizInterface() {
  const { quizId } = useParams();

  const [quiz, setQuiz] = useState();

  useEffect(() => {
    axios
      .get(`http://localhost:4000/getQuizById/${quizId}`)
      .then((response) => {
        const { quiz } = response.data;
        setQuiz(quiz);
      })
      .catch((error) => {
        console.log(error);
      });
  });

  return (
    <div
      style={{
        height: "100vh",
        width: "100vw",
        backgroundColor: "#041325",
        display: "flex",
      }}
    >
      <div
        style={{
          height: "80vh",
          width: "90vw",
          backgroundColor: "#FFFFFF",
          marginLeft: "5vw",
          marginRight: "5vw",
          marginTop: "9.7vh",
          marginBottom: "10.3vh",
          borderRadius: "15px",
        }}
      >
        <div style={{ display: "flex",marginTop:"5vh",marginLeft:"3vw" }}>
          <p style={{ color: "#474444", fontWeight: "600", fontSize: "2em" }}>
            01/04
          </p>
          <p style={{}}>00:10s</p>
        </div>

        <div>
          <p>Your question text comes here, its a sample text.</p>
        </div>
      </div>
    </div>
  );
}

export default LiveQuizInterface;
