import React from "react";

function PollQuestion({ question, option1, option2, option3 }) {
  return (
    <div style={{ height: "22vh", width: "100%", marginTop: "2vh" }}>
      <p style={{ fontWeight: "600", fontSize: "1.3em", letterSpacing: "1px" }}>
        Q.1 Question place holder for analysis ?
      </p>

      <div
        style={{
          display: "flex",
          gap: "4vw",
          marginLeft: "2vw",
          marginTop: "4vh",
        }}
      >
        <div
          style={{
            color: "#474444",
            fontWeight: "600",
            backgroundColor: "white",
            borderRadius: "5px",
            paddingLeft: "1.5vw",
            paddingRight: "1.5vw",
            width: "14vw",
            height: "12vh",
          }}
        >
          <p
            style={{
              fontSize: "2em",
              paddingTop: "2.7vh",
              paddingBottom: "2vh",
            }}
          >
            {option1}
            <span
              style={{
                fontSize: "0.5em",
                marginLeft: "1vw",
                marginTop: "-1vh",
              }}
            >
              Option 1
            </span>
          </p>
        </div>

        <div
          style={{
            color: "#474444",
            fontWeight: "600",
            backgroundColor: "white",
            borderRadius: "5px",
            paddingLeft: "1.5vw",
            paddingRight: "1.5vw",
            width: "14vw",
            height: "12vh",
          }}
        >
          <p
            style={{
              fontSize: "2em",
              paddingTop: "2.7vh",
              paddingBottom: "2vh",
            }}
          >
            {option2}
            <span style={{ fontSize: "0.5em", marginLeft: "1vw" }}>
              Option 2
            </span>
          </p>
        </div>

        <div
          style={{
            color: "#474444",
            fontWeight: "600",
            backgroundColor: "white",
            borderRadius: "5px",
            paddingLeft: "1.5vw",
            paddingRight: "1.5vw",
            width: "14vw",
            height: "12vh",
          }}
        >
          <p
            style={{
              fontSize: "2em",
              paddingTop: "2.7vh",
              paddingBottom: "2vh",
            }}
          >
            {option3}
            <span style={{ fontSize: "0.5em", marginLeft: "1vw" }}>
              Option 3
            </span>
          </p>
        </div>

        <div
          style={{
            color: "#474444",
            fontWeight: "600",
            backgroundColor: "white",
            borderRadius: "5px",
            paddingLeft: "1.5vw",
            paddingRight: "1.5vw",
            width: "14vw",
            height: "12vh",
          }}
        >
          <p
            style={{
              fontSize: "2em",
              paddingTop: "2.7vh",
              paddingBottom: "2vh",
            }}
          >
            {option4}
            <span style={{ fontSize: "0.5em", marginLeft: "1vw" }}>
              Option 4
            </span>
          </p>
        </div>
      </div>
    </div>
  );
}

export default PollQuestion;
