import React from "react";

function EachQuestion({ setIsAddQuestionsPopupOpen }) {
  const handleCancelBtn = () => {
    setIsAddQuestionsPopupOpen(false);
  };

  const handleCreateQuizBtn = () => {
    setIsQuizPublishedPopupOpen(true);
    setIsAddQuestionsPopupOpen(false);
  };

  return (
    <div>
      <div style={{ height: "8vh", width: "50vw" }}>
        <input
          style={{
            width: "46.5vw",
            marginLeft: "2vw",
            marginTop: "3vh",
            marginRight: "2vw",
            paddingTop: "1.5vh",
            paddingBottom: "1.5vh",
            paddingLeft: "1vw",
            border: "none",
            fontSize: "1.2em",
            borderRadius: "10px",
            boxShadow: "0px 0px 25px 0px #00000026",
          }}
          type="text"
          placeholder="Poll Question "
        ></input>
      </div>

      <div
        style={{
          marginTop: "3.5vh",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          height: "6vh",
          width: "46vw",
          marginLeft: "2.5vw",
          marginRight: "2.5vw",
          color: "#9F9F9F",
          fontSize: "1.2em",
        }}
      >
        <div style={{ marginRight: "1vw" }}>
          <p>Option Type </p>
        </div>

        <div style={{ marginRight: "1vw" }}>
          <input type="radio" name="options" id="option1" />
          <label htmlFor="Text"> Text</label>
        </div>

        <div style={{ marginRight: "1vw" }}>
          <input type="radio" name="options" id="option2" />
          <label htmlFor="ImageUrl"> Image URL</label>
        </div>

        <div>
          <input type="radio" name="options" id="option3" />
          <label htmlFor="TextAndImageUrl"> Text & Image URL</label>
        </div>
      </div>

      <div
        style={{ backgroundColor: "orange", height: "30vh", width: "50vw" }}
      ></div>

      <div
        style={{
          height: "6.5vh",
          width: "46vw",
          display: "flex",
          justifyContent: "space-between",
          marginTop: "4vh",
          marginLeft: "2vw",
          marginRight: "2vw",
        }}
      >
        <button
          onClick={handleCancelBtn}
          style={{
            color: "#474444",
            boxShadow: "0px 0px 15px 0px #00000040",
            background: "none",
            fontWeight: "600",
            fontSize: "1.2em",
            width: "17vw",
            borderRadius: "10px",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            border: "none",
            cursor: "pointer",
          }}
        >
          Cancel
        </button>
        <button onClick={handleCreateQuizBtn}
          style={{
            color: "#FFFFFF",
            backgroundColor: "#60B84B",
            fontWeight: "600",
            fontSize: "1.2em",
            width: "17vw",
            borderRadius: "10px",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            border: "none",
            cursor: "pointer",
          }}
        >
          Create Quiz
        </button>
      </div>
    </div>
  );
}

export default EachQuestion;
