import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function LiveQuizInterface() {
  const { quizId } = useParams();

  const [quiz, setQuiz] = useState();

  useEffect(() => {
    axios
      .get(`http://localhost:4000/getQuizById/${quizId}`)
      .then((response) => {
        const { quiz } = response.data;
        setQuiz(quiz);
      })
      .catch((error) => {
        console.log(error);
      });
  });

  return (
    <div
      style={{
        height: "100vh",
        width: "100vw",
        backgroundColor: "#041325",
        display: "flex",
      }}
    >
      <div
        style={{
          height: "80vh",
          width: "90vw",
          backgroundColor: "#FFFFFF",
          marginLeft: "5vw",
          marginRight: "5vw",
          marginTop: "9.7vh",
          marginBottom: "10.3vh",
          borderRadius: "15px",
        }}
      >
        <div
          style={{
            display: "flex",
            marginTop: "5vh",
            marginLeft: "3vw",
            gap: "72vw",
          }}
        >
          <p style={{ color: "#474444", fontWeight: "600", fontSize: "2em" }}>
            01/04
          </p>
          <p
            style={{
              color: "#D60000",
              fontWeight: "600",
              fontSize: "2em",
              marginRight: "3vw",
            }}
          >
            00:10s
          </p>
        </div>

        <div style={{ marginTop: "5vh", textAlign: "center" }}>
          <p style={{ color: "#474444", fontSize: "2em", fontWeight: "600" }}>
            Your question text comes here, its a sample text.
          </p>
        </div>

        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            width: "50vw",
            marginLeft: "20vw",
            marginRight: "20vw",
            gap: "10vw",
            marginTop: "5vh",
          }}
        >
          <p
            style={{
              backgroundColor: "#F0F0F0",
              paddingTop: "4vh",
              paddingBottom: "4vh",
              borderRadius: "10px",
              width: "20vw",
              fontWeight: "600",
              fontSize: "1.6em",
              textAlign: "center",
            }}
          >
            Option 1
          </p>
          <p
            style={{
              backgroundColor: "#F0F0F0",
              paddingTop: "4vh",
              paddingBottom: "4vh",
              borderRadius: "10px",
              width: "20vw",
              fontWeight: "600",
              fontSize: "1.6em",
              textAlign: "center",
            }}
          >
            Option 1
          </p>
        </div>

        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            width: "50vw",
            marginLeft: "20vw",
            marginRight: "20vw",
            gap: "10vw",
            marginTop: "10vh",
          }}
        >
          <p
            style={{
              backgroundColor: "#F0F0F0",
              paddingTop: "4vh",
              paddingBottom: "4vh",
              borderRadius: "10px",
              width: "20vw",
              fontWeight: "600",
              fontSize: "1.6em",
              textAlign: "center",
            }}
          >
            Option 1
          </p>
          <p
            style={{
              backgroundColor: "#F0F0F0",
              paddingTop: "4vh",
              paddingBottom: "4vh",
              borderRadius: "10px",
              width: "20vw",
              fontWeight: "600",
              fontSize: "1.6em",
              textAlign: "center",
            }}
          >
            Option 1
          </p>
        </div>

        <div style={{}}>
          <button>NEXT</button>
        </div>
      </div>
    </div>
  );
}

export default LiveQuizInterface;
