import React, { useState } from "react";
import deleteicon from "../images/deleteIcon.png";

function OptionInputs() {
  const [options, setOptions] = useState([]);

  const handleAddOption = () => {
    if (options.length < 4) {
      const newOption = {
        id: options.length + 1,
        type: "Text",
        value: "",
        name: "optionType",
      };
      setOptions([...options, newOption]);
    }
  };

  const handleDeleteOption = (id) => {
    if (options.length > 2 && id > 2) {
      setOptions((prevOptions) =>
        prevOptions.filter((option) => option.id !== id)
      );
    }
  };

  const handleInputChange = (id, value) => {
    setOptions((prevOptions) =>
      prevOptions.map((option) =>
        option.id === id ? { ...option, value } : option
      )
    );
  };

  const handleRadioChange = (id) => {
    setOptions((prevOptions) =>
      prevOptions.map((option) =>
        option.id === id ? { ...option, type: "TextAndImageUrl" } : option
      )
    );
  };

  return (
    <div>
      {
        options.map((option) => (
          <div key={option.id} style={{ display: "flex", alignItems: "center" }}>
            <input
              type="radio"
              name={option.name}
              id={`option${option.id}`}
              onChange={() => handleRadioChange(option.id)}
            />
            <label htmlFor={`option${option.id}`}>
              <input
                style={{
                  width: "15vw",
                  paddingTop: "1vh",
                  paddingBottom: "1vh",
                  boxShadow: "0px 0px 25px 0px #00000026",
                  borderRadius: "10px",
                  border: "none",
                  fontSize: "1.1em",
                  paddingLeft: "1vw",
                  marginLeft: "1vw",
                  marginTop: "3vh",
                }}
                type="text"
                placeholder="Text"
                value={option.value}
                onChange={(e) => handleInputChange(option.id, e.target.value)}
              />
            </label>
            {option.id > 2 && (
              <img
                style={{ marginLeft: "2vw", marginTop: "2vh", cursor: "pointer" }}
                src={deleteicon}
                alt="deleteicon"
                onClick={() => handleDeleteOption(option.id)}
              />
            )}
          </div>
        ))}

      { options.length < 4 && (
        <div>
          <button
            style={{
              color: "#9F9F9F",
              paddingTop: "1vh",
              paddingBottom: "1vh",
              width: "16vw",
              boxShadow: "0px 0px 25px 0px #00000026",
              borderRadius: "10px",
              border: "none",
              background: "none",
              fontSize: "1.2em",
              marginLeft: "2vw",
              marginTop: "5vh",
            }}
            onClick={handleAddOption}
          >
            Add option
          </button>
        </div>
      )}
    </div>
  );
}

export default OptionInputs;
