import React, { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function DeleteQuizPopup({ quizId, onClose }) {
  const token = localStorage.getItem("jwtToken");

  const headers = {
    Authorization: `Bearer ${token}`,
  };

  const [error, setError] = useState("");

  const userId = localStorage.getItem("userId");

  const handleDeleteQuiz = () => {
    axios
      .delete(`http://localhost:4000/quizDeletion/${userId}{quizId}`, { headers })
      .then((response) => {
        onClose();
        toast.success("Quiz Deleted Successfully");
      })
      .catch((error) => {
        if (
          error.response &&
          error.response.data &&
          error.response.data.error
        ) {
          setError(error.response.data.error);
        } else {
          setError("An error occurred while deleting quiz");
        }
        console.log(error);
      });
  };

  const handleCancelBtn = () => {
    onClose();
  };

  return (
    <div
      style={{
        paddingTop: "3vh",
        paddingBottom: "6vh",
        width: "57vw",
        marginLeft: "3vw",
        backgroundColor: "white",
        borderRadius: "10px",
      }}
    >
      <div
        style={{
          fontSize: "2.5em",
          fontWeight: "600",
          color: "#474444",
          textAlign: "center",
          paddingTop: "2vh",
        }}
      >
        <p>Are you confirm you</p>
        <p> want to delete ?</p>
      </div>

      <div
        style={{
          display: "flex",
          marginLeft: "6vw",
          marginTop: "8vh",
          gap: "5vw",
          marginRight: "3vw",
        }}
      >
        <button
          onClick={handleDeleteQuiz}
          style={{
            height: "6vh",
            width: "20vw",
            borderRadius: "10px",
            color: "#FFFFFF",
            fontSize: "1.2em",
            backgroundColor: "#FF4B4B",
            border: "none",
            cursor: "pointer",
          }}
        >
          Confirm Delete
        </button>
        <button
          onClick={handleCancelBtn}
          style={{
            height: "6vh",
            width: "20vw",
            borderRadius: "10px",
            color: "#474444",
            fontSize: "1.2em",
            backgroundColor: "white",
            boxShadow: "0px 0px 15px 0px #00000040",
            border: "none",
            cursor: "pointer",
          }}
        >
          Cancel
        </button>
      </div>
      {error && (
        <p
          style={{
            color: "red",
            marginTop: "4vh",
            textAlign:"center",
            fontSize: "1.3em",
          }}
        >
          {error}
        </p>
      )}
    </div>
  );
}

export default DeleteQuizPopup;
