import React, { useState } from "react";
import plusicon from "../images/plusicon.png";
import EachQuestion from "./EachQuestion";

function AddQuestionsPopup({
  setIsAddQuestionsPopupOpen,
  setIsQuizPublishedPopupOpen,
}) {
  const [selectedQuestion, setSelectedQuestion] = useState(1);
  const [questions, setQuestions] = useState([1]);

  const addQuestion = () => {
    if (questions.length < 5) {
      const newQuestionNumber = selectedQuestion + 1;
      setQuestions([...questions, newQuestionNumber]);

      setSelectedQuestion(newQuestionNumber);
    }
  };

  return (
    <div
      style={{
        paddingBottom: "7vh",
        paddingTop: "4vh",
        paddingRight: "2.5vw",
        paddingLeft: "2.5vw",
        marginLeft: "3vw",
        backgroundColor: "white",
        borderRadius: "10px",
        width: "50vw",
      }}
    >
      <div
        style={{
          height: "10vh",
          width: "50vw",
          display: "flex",
          gap
        }}
      >
        {questions.map((questionNumber, index) => (
          <div key={questionNumber}>
            <p
              style={{
                marginLeft: "2vw",
                marginTop: "1vh",
                fontSize: "1.3em",
                color: "#9F9F9F",
                display: "inline-block",
                borderRadius: "50%",
                boxShadow: "0px 0px 10px 0px #00000026",
                width: "8vh",
                height: "8vh",
                textAlign: "center",
                lineHeight: "8vh",
                fontWeight: "600",
                marginRight: "1.5vw",
              }}
            >
              {questionNumber}
            </p>
            {index === questions.length - 1 && questions.length < 5 && (
              <span>
                <img onClick={addQuestion} src={plusicon} alt="plusicon" />
              </span>
            )}
          </div>
        ))}

        <p
          style={{
            color: "#9F9F9F",
            fontSize: "1.2em",
            lineHeight: "9vh",
            marginRight: "0.5vw",
          }}
        >
          Max 5 questions
        </p>
      </div>

      <EachQuestion
        key={selectedQuestion}
        setIsAddQuestionsPopupOpen={setIsAddQuestionsPopupOpen}
        setIsQuizPublishedPopupOpen={setIsQuizPublishedPopupOpen}
      />
    </div>
  );
}

export default AddQuestionsPopup;
