import React, { useState } from "react";
import Timer from "./Timer";
import OptionInputsText from "./OptionInputsText";
import OptionInputsImage from "./OptionInputsImage";
import OptionInputsTextAndImage from "./OptionInputsTextAndImage";

function EachQuestion({
  questionNumber,
  selected,
  questionsArray,
  onQuestionChange,
}) {
  const [optionType, setOptionType] = useState("Text");
  const [questionValue, setQuestionValue] = useState("");

  const handleQuestionChange = (e) => {
    const updatedQuestion = {
      ...questionsArray,
      question: e.target.value,
      optionType: optionType,
    };

    onQuestionChange(updatedQuestion);
    setQuestionValue(e.target.value);
  };

  const renderOptionInputs = () => {
    switch (optionType) {
      case "Text":
        return <OptionInputsText questionsArray={questionsArray} poll="false" />;
      case "ImageUrl":
        return <OptionInputsImage questionsArray={questionsArray} poll="false" />;
      case "TextAndImageUrl":
        return <OptionInputsTextAndImage questionsArray={questionsArray} poll="false" />;
      default:
        return null;
    }
  };

  return (
    <div style={{ display: selected ? "block" : "none" }}>
      <div style={{ height: "8vh", width: "50vw" }}>
        <input
          style={{
            width: "46.5vw",
            marginLeft: "2vw",
            marginTop: "3vh",
            marginRight: "2vw",
            paddingTop: "1.5vh",
            paddingBottom: "1.5vh",
            paddingLeft: "1vw",
            border: "none",
            fontSize: "1.2em",
            borderRadius: "10px",
            boxShadow: "0px 0px 25px 0px #00000026",
          }}
          type="text"
          value={questionValue}
          onChange={handleQuestionChange}
          placeholder={`Question ${questionNumber}`}
        />
      </div>

      <div
        style={{
          marginTop: "4.5vh",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          height: "6vh",
          width: "46vw",
          marginLeft: "2.5vw",
          marginRight: "2.5vw",
          color: "#9F9F9F",
          fontSize: "1.2em",
        }}
      >
        <div style={{ marginRight: "1vw" }}>
          <p>Option Type</p>
        </div>

        <div style={{ marginRight: "1vw" }}>
          <input
            type="radio"
            name={`optionType-${questionNumber}`}
            id={`Text-${questionNumber}`}
            checked={optionType === "Text"}
            onClick={() => setOptionType("Text")}
          />
          <label htmlFor={`Text-${questionNumber}`}>Text</label>
        </div>

        <div style={{ marginRight: "1vw" }}>
          <input
            type="radio"
            name={`optionType-${questionNumber}`}
            id={`ImageUrl-${questionNumber}`}
            checked={optionType === "ImageUrl"}
            onClick={() => setOptionType("Text")}
          />
          <label htmlFor={`ImageUrl-${questionNumber}`}>Image URL</label>
        </div>

        <div>
          <input
            type="radio"
            name={`optionType-${questionNumber}`}
            id={`TextAndImageUrl-${questionNumber}`}
            checked={optionType === "TextAndImageUrl"}
            onChange={() => setOptionType("TextAndImageUrl")}
          />
          <label htmlFor={`TextAndImageUrl-${questionNumber}`}>Text & Image URL</label>
        </div>
      </div>

      <div
        style={{
          height: "38vh",
          width: "48vw",
          display: "flex",
          marginLeft: "1vw",
          marginRight: "1vw",
          justifyContent: "space-between",
          marginTop: "3vh",
          overflow: "auto",
          marginBottom: "4vh",
        }}
      >
        <div style={{ width: "35vw" }}>{renderOptionInputs()}</div>
        <div style={{ width: "10vw" }}>
          <Timer />
        </div>
      </div>
    </div>
  );
}

export default EachQuestion;
