import React, { useState } from "react";

function CreateQuizPopup({
  setIsCreateQuizPopupOpen,
  setIsAddQuestionsPopupOpen,
  setQuizName,
  setQuizType, 
}) {
 

  const handleCancelBtn = () => {
    setIsCreateQuizPopupOpen(false);
  };

  const [isQnAClicked, setIsQnAClicked] = useState(false);
  const [isPollTypeClicked, setIsPollTypeClicked] = useState(false);


  const handleQnABtn = () => {
    setIsQnAClicked(true);
    setIsPollTypeClicked(false);
    setQuizType("QnA");
  };

  const handlePollTypeBtn = () => {
    setIsPollTypeClicked(true);
    setIsQnAClicked(false);
    setQuizType("Poll Type");
  };

  const handleContinueClicked = () => {
    setIsAddQuestionsPopupOpen(true);
    setIsCreateQuizPopupOpen(false);
  };

  return (
    <div
      style={{
        paddingBottom: "7vh",
        paddingTop: "6vh",
        paddingRight: "2.5vw",
        paddingLeft: "2.5vw",
        marginLeft: "3vw",
        backgroundColor: "white",
        borderRadius: "10px",
        width: "40vw",
      }}
    >
      <input
        style={{
          marginTop: "3vh",
          marginLeft: "1vw",
          marginRight: "1vw",
          paddingTop: "1vh",
          paddingBottom: "1vh",
          paddingLeft: "1vw",
          paddingRight: "1vw",
          height: "5vh",
          width: "35vw",
          fontSize: "1.2em",
          borderRadius: "10px",
          border: "none",
          boxShadow: "0px 0px 25px 0px #00000026",
        }}
        type="text"
        placeholder="Quiz name "
        value={quizName}
        onChange={(e) => setQuizName(e.target.value)}
      ></input>

      <div
        style={{
          display: "flex",
          marginLeft: "2.5vw",
          marginTop: "5vh",
          fontWeight: "600",
          gap: "5vw",
          marginRight: "3vw",
        }}
      >
        <p style={{ color: "#9F9F9F", fontSize: "1.2em", marginTop: "1vh" }}>
          Quiz Type
        </p>
        <button
          onClick={handleQnABtn}
          style={{
            color: isQnAClicked ? "#FFFFFF" : "#9F9F9F",
            boxShadow: "0px 0px 25px 0px #00000026",
            border: "none",
            backgroundColor: isQnAClicked ? "#60B84B" : "#FFFFFF",
            borderRadius: "10px",
            paddingLeft: "2.5vw",
            paddingRight: "2.5vw",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            fontWeight: "600",
            fontSize: "1.1em",
            cursor: "pointer",
          }}
        >
          Q & A
        </button>
        <button
          onClick={handlePollTypeBtn}
          style={{
            color: isPollTypeClicked ? "#FFFFFF" : "#9F9F9F",
            boxShadow: "0px 0px 25px 0px #00000026",
            border: "none",
            backgroundColor: isPollTypeClicked ? "#60B84B" : "#FFFFFF",
            borderRadius: "10px",
            paddingLeft: "2vw",
            paddingRight: "2vw",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            fontWeight: "600",
            fontSize: "1.1em",
            cursor: "pointer",
          }}
        >
          Poll Type
        </button>
      </div>
      <div
        style={{
          display: "flex",
          marginLeft: "2.5vw",
          marginTop: "8vh",
          gap: "4.3vw",
        }}
      >
        <button
          onClick={handleCancelBtn}
          style={{
            background: "none",
            boxShadow: "0px 0px 15px 0px #00000040",
            border: "none",
            color: "#474444",
            borderRadius: "10px",
            paddingTop: "1.3vh",
            paddingBottom: "1.3vh",
            fontWeight: "bold",
            width: "15vw",
            fontSize: "1.1em",
            cursor: "pointer",
          }}
        >
          Cancel
        </button>
        <button
          onClick={handleContinueClicked}
          style={{
            backgroundColor: "#60B84B",
            border: "none",
            color: "#FFFFFF",
            borderRadius: "10px",
            paddingTop: "1.3vh",
            paddingBottom: "1.3vh",
            fontWeight: "bold",
            width: "15vw",
            fontSize: "1.1em",
            cursor: "pointer",
            marginRight: "3vw",
          }}
        >
          Continue
        </button>
      </div>
    </div>
  );
}

export default CreateQuizPopup;
