import React from "react";
import trophyimage from "../images/trophyimage.png";
import styles from "./CompletedQuiz.module.css";

function CompletedQuiz({ type, correctAnswer, totalQuestions }) {
  if (type === "QnA") {
    return (
      <div className={container}>
        <div
          style={{
            height: "82vh",
            width: "90vw",
            backgroundColor: "#FFFFFF",
            marginLeft: "5vw",
            marginRight: "5vw",
            marginTop: "9vh",
            marginBottom: "9vh",
            borderRadius: "15px",
            boxShadow: "0px 0px 30px 0px #00000026",
            textAlign: "center",
          }}
        >
          <p
            style={{
              marginTop: "7vh",
              color: "#474444",
              fontSize: "2.5em",
              fontWeight: "600",
            }}
          >
            Congrats Quiz is completed
          </p>

          <p>
            <img
              style={{ height: "50vh", width: "25vw" }}
              src={trophyimage}
              alt="trophyimage"
            />
          </p>

          <p
            style={{
              marginTop: "1vh",
              color: "#474444",
              fontSize: "2.5em",
              fontWeight: "600",
            }}
          >
            Your Score is{" "}
            <span style={{ color: "#60B84B" }}>
              0{correctAnswer}/0{totalQuestions}
            </span>
          </p>
        </div>
      </div>
    );
  } else {
    return (
      <div className={container}>
        <div
          style={{
            height: "82vh",
            width: "90vw",
            backgroundColor: "#FFFFFF",
            marginLeft: "5vw",
            marginRight: "5vw",
            marginTop: "9vh",
            marginBottom: "9vh",
            borderRadius: "15px",
            boxShadow: "0px 0px 30px 0px #00000026",
            textAlign: "center",
          }}
        >
          <p
            style={{
              color: "#474444",
              fontSize: "4.5em",
              fontWeight: "600",
              margin: "20vh 0",
            }}
          >
            <p>Thank you</p> <p>for participating in </p>
            <p>the Poll</p>
          </p>
        </div>
      </div>
    );
  }
}

export default CompletedQuiz;
