import React, { useState, useEffect } from "react";
import deleteicon from "../images/deleteIcon.png";

function OptionInputsText({
  questionsArray,
  onQuestionChange,
  quizType,
  editableQuiz,
}) {
  const [options, setOptions] = useState([]);

  useEffect(() => {
    if (questionsArray && questionsArray.optionValues) {
      // Initialize options with the options from questionsArray in editableQuiz
      setOptions(questionsArray.optionValues || []);
    }
  }, [questionsArray]);

  const handleAddOption = () => {
    if (options.length < 4) {
      const newOption = {
        id: options.length + 1,
        value: "",
      };
      const updatedOptions = [...options, newOption];

      const updatedQuestion = {
        ...questionsArray,
        optionValues: updatedOptions.map((option) => ({
          value: option.value,
          imageUrl: option.imageUrl,
        })),
      };

      onQuestionChange(updatedQuestion);
      setOptions(updatedOptions);
    }
  };

  const handleDeleteOption = (id) => {
    if (options.length > 2 && id > 2) {
      const updatedOptions = options.filter((option) => option.id !== id);
      const updatedQuestion = {
        ...questionsArray,
        optionValues: updatedOptions.map((option) => ({
          value: option.value,
          imageUrl: option.imageUrl,
        })),
      };

      onQuestionChange(updatedQuestion);
      setOptions(updatedOptions);
    }
  };

  const handleInputChange = (id, value) => {
    const updatedOptions = options.map((option) =>
      option.id === id ? { ...option, value } : option
    );

    const updatedQuestion = {
      ...questionsArray,
      optionValues: updatedOptions.map((option) => ({
        value: option.value,
        imageUrl: option.imageUrl,
      })),
    };

    onQuestionChange(updatedQuestion);
    setOptions(updatedOptions);
  };

  return (
    <div>
      {options.map((option) => (
        <div key={option.id} style={{ display: "flex", alignItems: "center" }}>
          {quizType === "QnA" && (
            <input
              type="radio"
              name="optionsGroup"
              id={`option${option.id}`}
              onChange={() => handleInputChange(option.id, option.value)}
            />
          )}
          <label htmlFor={`option${option.id}`}>
            <input
              style={{
                width: "15vw",
                paddingTop: "1vh",
                paddingBottom: "1vh",
                boxShadow: "0px 0px 25px 0px #00000026",
                borderRadius: "10px",
                border: "none",
                fontSize: "1.1em",
                paddingLeft: "1vw",
                marginLeft: "1vw",
                marginTop: "3vh",
                backgroundColor: option.id === 2 ? "#60B84B" : "white", // Color for better visibility, adjust as needed
                color: option.id === 2 ? "white" : "#9F9F9F", // Color for better visibility, adjust as needed
              }}
              type="text"
              placeholder="Text"
              value={option.value}
              onChange={(e) => handleInputChange(option.id, e.target.value)}
            />
          </label>
          {option.id > 2 && (
            <img
              style={{ marginLeft: "2vw", marginTop: "2vh", cursor: "pointer" }}
              src={deleteicon}
              alt="deleteicon"
              onClick={() => handleDeleteOption(option.id)}
            />
          )}
        </div>
      ))}

      {options.length < 4 && (
        <div>
          <button
            style={{
              color: "#9F9F9F",
              paddingTop: "1vh",
              paddingBottom: "1vh",
              width: "16vw",
              boxShadow: "0px 0px 25px 0px #00000026",
              borderRadius: "10px",
              border: "none",
              background: "none",
              fontSize: "1.2em",
              marginLeft: "2vw",
              marginTop: "5vh",
            }}
            onClick={handleAddOption}
          >
            Add option
          </button>
        </div>
      )}
    </div>
  );
}

export default OptionInputsText;
