import React, { useState, useEffect } from "react";
import TrendingQuizBox from "./TrendingQuizBox";
import axios from "axios";

function Dashboard() {
  const [totalQuizzes, setTotalQuizzes] = useState();
  const [totalQuestions, setTotalQuestions] = useState();
  const [totalImpressions, setTotalImpressions] = useState();
  const [quizzes, setQuizzes] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:4000/getAllQuizzes")
      .then((response) => {
        const {
          totalQuizzes,
          totalQuestions,
          totalImpressions,
          impressionsSortedQuizzes,
        } = response.data;

        setTotalQuizzes(totalQuizzes);
        setTotalQuestions(totalQuestions);
        setTotalImpressions(totalImpressions);
        setQuizzes(impressionsSortedQuizzes);
        console.log(quizzes);
      })
      .catch((error) =>
        console.error("Error fetching formatted quizzes:", error)
      );
  });

  return (
    <div>
      <div
        style={{
          height: "40vh",
          width: "76vw",
          marginBottom: "3vh",
          marginRight: "1vw",
          display: "flex",
          fontSize: "2em",
          fontWeight: "600",
        }}
      >
        <div
          style={{
            height: "12vh",
            width: "16vw",
            marginTop: "15vh",
            marginLeft: "4vw",
            backgroundColor: "#FFFFFF",
            borderRadius: "10px",
            color: "#FF5D01",
            paddingLeft: "4vw",
            paddingRight: "2vw",
            paddingTop: "2vh",
            paddingBottom: "5vh",
          }}
        >
          <p>
            <span style={{ fontSize: "1.4em", marginRight: "1.5vw" }}>
              {totalQuizzes}
            </span>
            Quiz
          </p>
          <p>Created </p>
        </div>

        <div
          style={{
            height: "12vh",
            width: "16vw",
            marginTop: "15vh",
            marginLeft: "4vw",
            backgroundColor: "#FFFFFF",
            borderRadius: "10px",
            color: "#60B84B",
            paddingLeft: "2vw",
            paddingRight: "2vw",
            paddingTop: "2vh",
            paddingBottom: "5vh",
          }}
        >
          <p>
            <span style={{ fontSize: "1.4em", marginRight: "1.5vw" }}>
              {totalQuestions}
            </span>
            Questions
          </p>
          <p>Created </p>
        </div>
        <div
          style={{
            height: "12vh",
            width: "16vw",
            marginTop: "15vh",
            marginLeft: "4vw",
            backgroundColor: "#FFFFFF",
            borderRadius: "10px",
            color: "#5076FF",
            paddingLeft: "4vw",
            paddingRight: "2vw",
            paddingTop: "2vh",
            paddingBottom: "5vh",
          }}
        >
          <p>
            <span style={{ fontSize: "1.4em", marginRight: "1.5vw" }}>
              {totalImpressions}
            </span>
            Total
          </p>
          <p>Impressions </p>
        </div>
      </div>
      <div
        style={{
          marginTop: "9vh",
          marginLeft: "4vw",
          height: "60vh",
          width: "80vw",
          marginBottom: "3vw",
        }}
      >
        <p style={{ color: "#474444", fontSize: "2em", fontWeight: "600" }}>
          Trending Quizs
        </p>
        <div
          style={{
            height: "32vh",
            width: "75vw",
            marginLeft: "-1.8vw",
            marginRight: "1.2vw",
            marginTop: "4vh",
            overflow: "auto",
            display: "flex",
            flexWrap: "wrap",
          }}
        >
          {quizzes.map((quiz)=>(
            <TrendingQuizBox quizName={quiz.name} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
