import React, { useState } from "react";
import SignUpDetails from "./SignUpDetails";
import LoginDetails from "./LoginDetails";
import styles from "./SignupLogin.module.css";

function SignupLoginPage() {
  const [isSignUpClicked, setIsSignUpClicked] = useState(true);
  const [isLoginClicked, setIsLoginClicked] = useState(false);

  const handleSignUpButton = () => {
    setIsSignUpClicked(true);
    setIsLoginClicked(false);
  };

  const handleLoginButton = () => {
    setIsLoginClicked(true);
    setIsSignUpClicked(false);
  };

  const handleSignUpSuccess = () => {
    setIsSignUpClicked(false);
    setIsLoginClicked(true);
  };

  return (
    <div className={styles.container}>
      <div className={styles.box}>
        <div className={styles.header}>
          <p className={styles.headerText}>QUIZZIE</p>
        </div>

        <div
          style={{
            height: "11%",
            width: "100%",
            display: "flex",
            marginTop: "2.7vh",
          }}
        >
          <button
            onClick={handleSignUpButton}
            style={{
              height: "65%",
              width: "15%",
              background: "none",
              border: "none",
              marginTop: "2vh",
              marginLeft: "25vw",
              color: "#474444",
              fontWeight: "bold",
              fontSize: "1.3em",
              borderRadius: "10px",
              boxShadow: isSignUpClicked ? " 0px 0px 50px 0px #0019FF3D" : "",
            }}
          >
            Sign Up
          </button>
          <button
            onClick={handleLoginButton}
            style={{
              height: "65%",
              width: "15%",
              background: "none",
              border: "none",
              marginTop: "2vh",
              marginLeft: "8vw",
              color: "#474444",
              fontWeight: "bold",
              fontSize: "1.3em",
              borderRadius: "10px",
              boxShadow: isLoginClicked ? "0px 0px 50px 0px #0019FF3D" : "",
            }}
          >
            Log In
          </button>
        </div>

        <div
          style={{
            height: "50%",
            width: "100%",
            marginTop: "4vh",
          }}
        >
          {isLoginClicked ? (
            <LoginDetails />
          ) : (
            <SignUpDetails onSignUpSuccess={handleSignUpSuccess} />
          )}
        </div>
      </div>
    </div>
  );
}

export default SignupLoginPage;
