import React, { useState } from "react";

function Timer() {
  const [isTimerOff, setIsTimerOff] = useState(false);
  const [isTimer5sec, setIsTimer5sec] = useState(false);
  const [isTimer10sec, setIsTimer10sec] = useState(false);

  const handleTimerOff=()=>{
    setIsTimerOff(true);
    setIsTimer5sec(false);
    setIsTimer10sec(false);
  }

  const handleTimer5sec=()=>{
    setIsTimerOff(false);
    setIsTimer5sec(true);
    setIsTimer10sec(false);
  }

  const handleTimer10sec=()=>{
    setIsTimerOff(false);
    setIsTimer5sec(false);
    setIsTimer10sec(true);
  }

  return (
    <div
      style={{
        width: "100%",
        textAlign: "center",
        marginTop: "3.5vh",
      }}
    >
      <p style={{ color: "#9F9F9F", fontSize: "1.2em" }}> Timer</p>
      <p>
        <button onClick={handleTimerOff}
          style={{
            color: isTimerOff? "#FFFFFF":"#9F9F9F",
            marginTop: "3vh",
            border: "none",
            width: "6vw",
            borderRadius: "10px",
            boxShadow: "0px 0px 15px 0px #00000026",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            fontSize: "0.9em",
            background: isTimerOff?"#D60000":"none",
          }}
        >
          OFF
        </button>
      </p>
      <p>
        <button onClick={handleTimer5sec}
          style={{
            color: isTimer5sec? "#FFFFFF":"#9F9F9F",
            marginTop: "3vh",
            border: "none",
            width: "6vw",
            borderRadius: "10px",
            boxShadow: "0px 0px 15px 0px #00000026",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            fontSize: "0.9em",
            background: isTimer5sec?"#D60000":"none",
          }}
        >
          5 sec
        </button>
      </p>
      <p>
        <button onClick={handleTimer10sec}
          style={{
            color: "#9F9F9F",
            marginTop: "3vh",
            border: "none",
            width: "6vw",
            borderRadius: "10px",
            boxShadow: "0px 0px 15px 0px #00000026",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            fontSize: "0.9em",
            background: isTimer5sec?"#D60000":"none",
          }}
        >
          10 sec
        </button>
      </p>
    </div>
  );
}

export default Timer;
