import React, { useState, useEffect } from "react";
import SideHeader from "./SideHeader";
import TrendingQuizBox from "./TrendingQuizBox";
import axios from "axios";
import CreateQuizPopup from "./CreateQuizPopup";
import AddQuestionsPopup from "./AddQuestionsPopup";
import QuizPublishedPopup from "./QuizPublishedPopup";

function DashboardPage() {
  const [isCreateQuizPopupOpen, setIsCreateQuizPopupOpen] = useState(false);
  const [isAddQuestionsPopupOpen, setIsAddQuestionsPopupOpen] = useState(false);
  const [isQuizPublishedPopupOpen, setIsQuizPublishedPopupOpen] =
    useState(false);

  const [totalQuizzes, setTotalQuizzes] = useState();
  const [totalQuestions, setTotalQuestions] = useState();
  const [totalImpressions, setTotalImpressions] = useState();
  const [quizzes, setQuizzes] = useState([]);

  const [newlyCreatedQuizId, setNewlyCreatedQuizId] = useState("");

  const token = localStorage.getItem("jwtToken");

  const headers = {
    Authorization: `Bearer ${token}`,
  };

  const userId = localStorage.getItem("userId");

  const [error, setError] = useState("");

  useEffect(() => {
    axios
      .get(`http://localhost:4000/getAllQuizzes/${userId}`, { headers })
      .then((response) => {
        const {
          totalQuizzes,
          totalQuestions,
          totalImpressions,
          impressionsSortedQuizzes,
        } = response.data;

        setTotalQuizzes(totalQuizzes);
        if (totalQuizzes === 0) {
          setError(
            "You haven't created any Quiz, Click on Create Quiz to create your first Quiz"
          );
        }
        setTotalQuestions(totalQuestions);
        setTotalImpressions(totalImpressions);
        setQuizzes(impressionsSortedQuizzes);
      })
      .catch((error) => {
        if (
          error.response &&
          error.response.data &&
          error.response.data.error
        ) {
          setError(error.response.data.error);
        } else {
          setError("An error occurred fetching quizzes");
        }
        console.log(error);
      });
    // eslint-disable-next-line
  }, [quizzes]);

  const [quizName, setQuizName] = useState("");
  const [quizType, setQuizType] = useState("");
  const [editableQuiz, setEditableQuiz] = useState(null);

  return (
    <div
      style={{
        display: "flex",
        height: "100vh",
        width: "100vw",
        overflow: "hidden",
      }}
    >
      <div
        style={{
          height: "100vh",
          width: "20vw",
          backgroundColor: "#FFFFFF",
          boxShadow: "0px 4px 4px 0px #00000040",
        }}
      >
        <SideHeader
          option="dashboard"
          setIsCreateQuizPopupOpen={setIsCreateQuizPopupOpen}
        />
      </div>
      <div
        style={{
          height: "100vh",
          width: "80vw",
          backgroundColor: "#EDEDED",
        }}
      >
        <div>
          <div
            style={{
              height: "40vh",
              width: "76vw",
              marginBottom: "3vh",
              marginRight: "1vw",
              display: "flex",
              fontSize: "2em",
              fontWeight: "600",
            }}
          >
            <div
              style={{
                height: "12vh",
                width: "16vw",
                marginTop: "15vh",
                marginLeft: "4vw",
                backgroundColor: "#FFFFFF",
                borderRadius: "10px",
                color: "#FF5D01",
                paddingTop: "2vh",
                paddingBottom: "5vh",
                textAlign: "center",
              }}
            >
              <p>
                <span style={{ fontSize: "1.4em", marginRight: "1.5vw" }}>
                  {totalQuizzes}
                </span>
                Quiz
              </p>
              <p>Created </p>
            </div>

            <div
              style={{
                height: "12vh",
                width: "16vw",
                marginTop: "15vh",
                marginLeft: "4vw",
                backgroundColor: "#FFFFFF",
                borderRadius: "10px",
                color: "#60B84B",
                paddingTop: "2vh",
                paddingBottom: "5vh",
                textAlign: "center",
              }}
            >
              <p>
                <span style={{ fontSize: "1.4em", marginRight: "1.5vw" }}>
                  {totalQuestions}
                </span>
                Questions
              </p>
              <p>Created </p>
            </div>
            <div
              style={{
                height: "12vh",
                width: "16vw",
                marginTop: "15vh",
                marginLeft: "4vw",
                backgroundColor: "#FFFFFF",
                borderRadius: "10px",
                color: "#5076FF",
                paddingTop: "2vh",
                paddingBottom: "5vh",
                textAlign: "center",
              }}
            >
              <p>
                <span style={{ fontSize: "1.4em", marginRight: "1.5vw" }}>
                  {totalImpressions}
                </span>
                Total
              </p>
              <p
                style={{
                  textAlign: "left",
                  paddingLeft: "15%",
                  paddingRight: "15%",
                }}
              >
                Impressions{" "}
              </p>
            </div>
          </div>
          <div
            style={{
              marginTop: "9vh",
              marginLeft: "4vw",
              height: "60vh",
              width: "80vw",
              marginBottom: "3vw",
            }}
          >
            <p style={{ color: "#474444", fontSize: "2em", fontWeight: "600" }}>
              Trending Quizs
            </p>

            {error && (
              <p style={{ color: "red", marginTop: "3vh", fontSize: "1.3em" }}>
                {error}
              </p>
            )}

            <div
              style={{
                height: "32vh",
                width: "75vw",
                marginLeft: "-1.8vw",
                marginRight: "1.2vw",
                marginTop: "4vh",
                overflow: "auto",
                display: "flex",
                flexWrap: "wrap",
              }}
            >
              {quizzes.map((quiz) => (
                <TrendingQuizBox
                  quizName={quiz.name}
                  impressions={quiz.impressions}
                  createdAt={quiz.createdAt}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
      {isCreateQuizPopupOpen && (
        <div
          style={{
            position: "fixed",
            width: "100%",
            height: "100%",
            background: "#000000CF",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <CreateQuizPopup
            setIsCreateQuizPopupOpen={setIsCreateQuizPopupOpen}
            setIsAddQuestionsPopupOpen={setIsAddQuestionsPopupOpen}
            setQuizName={setQuizName}
            setQuizType={setQuizType}
          />
        </div>
      )}
      {isAddQuestionsPopupOpen && (
        <div
          style={{
            position: "fixed",
            width: "100%",
            height: "100%",
            background: "#000000CF",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <AddQuestionsPopup
            setIsAddQuestionsPopupOpen={setIsAddQuestionsPopupOpen}
            setIsQuizPublishedPopupOpen={setIsQuizPublishedPopupOpen}
            quizName={quizName}
            quizType={quizType}
            editableQuiz={editableQuiz}
            setEditableQuiz={setEditableQuiz}
            setNewlyCreatedQuizId={setNewlyCreatedQuizId}
          />
        </div>
      )}
      {isQuizPublishedPopupOpen && (
        <div
          style={{
            position: "fixed",
            width: "100%",
            height: "100%",
            background: "#000000CF",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <QuizPublishedPopup
            setIsQuizPublishedPopupOpen={setIsQuizPublishedPopupOpen}
            newlyCreatedQuizId={newlyCreatedQuizId}
          />
        </div>
      )}
    </div>
  );
}

export default DashboardPage;
