import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import CompletedQuiz from "./CompletedQuiz";

function LiveQuizInterface() {
  const { quizId } = useParams();

  const [quiz, setQuiz] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [timer, setTimer] = useState(null);
  const [timerActive, setTimerActive] = useState(false);
  const [correctAnswersCount, setCorrectAnswersCount] = useState(0);
  const [selectedOptionIndex, setSelectedOptionIndex] = useState(null);
  const [quizCompleted, setQuizCompleted] = useState(false);

  useEffect(() => {
    axios
      .get(`http://localhost:4000/getQuizById/${quizId}`)
      .then((response) => {
        const { quiz } = response.data;
        setQuiz(quiz);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [quizId]);

  const handleNextOrSubmit = () => {
    if (!quizCompleted) {
      const currentQuestion = quiz.questionsArray[currentQuestionIndex];

      if (
        selectedOptionIndex !== null &&
        currentQuestion.correctAnswer === selectedOptionIndex &&
        !timerActive
      ) {
        setCorrectAnswersCount((prevCount) => prevCount + 1);
      }

      if (currentQuestionIndex < quiz.questionsArray.length - 1) {
        setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
      } else {
        setQuizCompleted(true);
      }
      setSelectedOptionIndex(null);
    }
  };

  useEffect(() => {
    if (timer === 0) {
      setTimerActive(false);
      handleNextOrSubmit();
    }
  }, [timer]);

  useEffect(() => {
    if (quizCompleted) {
      clearInterval(timerInterval);
    }
  }, [quizCompleted]);

  useEffect(() => {
    if (quiz && quiz.questionsArray && quiz.questionsArray.length > 0) {
      setTimer(quiz.timer === "5 sec" ? 5 : quiz.timer === "10 sec" ? 10 : 0);
    }
  }, [currentQuestionIndex, quiz]);

  const handleOptionSelect = (index) => {
    setSelectedOptionIndex(index);
  };

  useEffect(() => {
   

    if (timerActive) {
      timerInterval = setInterval(() => {
        setTimer((prevTimer) => {
          if (prevTimer > 0) {
            return prevTimer - 1;
          } else {
            setTimerActive(false);
            clearInterval(timerInterval);
            handleNextOrSubmit();
            return 0;
          }
        });
      }, 1000);
    }

    return () => {
      clearInterval(timerInterval);
    };
  }, [timerActive, currentQuestionIndex, quizCompleted]);

  useEffect(() => {
    console.log(correctAnswersCount);
  }, [correctAnswersCount]);

  if (quizCompleted) {
    return <CompletedQuiz type="qna" correctAnswer={correctAnswersCount} />;
  }

  return (
    <div
      style={{
        height: "100vh",
        width: "100vw",
        backgroundColor: "#041325",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <div
        style={{
          height: "82vh",
          width: "90vw",
          backgroundColor: "#FFFFFF",
          margin: "9vh 5vw",
          borderRadius: "15px",
          boxShadow: "0px 0px 30px 0px #00000026",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        {quiz && quiz.questionsArray && quiz.questionsArray.length > 0 && (
          <div>
            <div
              style={{
                display: "flex",
                marginTop: "5vh",
                justifyContent: "space-between",
              }}
            >
              <p
                style={{
                  color: "#474444",
                  fontWeight: "600",
                  fontSize: "2em",
                  marginLeft: "3vw",
                }}
              >
                0{currentQuestionIndex + 1}/0{quiz.questionsArray.length}
              </p>
              <p
                style={{
                  color: "#D60000",
                  fontWeight: "600",
                  fontSize: "2em",
                  marginRight: "3vw",
                }}
              >
                00:{timer < 10 ? `0${timer}` : timer}s
              </p>
            </div>

            <div style={{ marginTop: "2.5vh", textAlign: "center" }}>
              <p
                style={{
                  color: "#474444",
                  fontSize: "2em",
                  fontWeight: "600",
                }}
              >
                {quiz.questionsArray[currentQuestionIndex].question}
              </p>
            </div>

            <div
              style={{
                display: "flex",
                flexWrap: "wrap",
                width: "50vw",
                marginLeft: "20vw",
                marginRight: "20vw",
                justifyContent: "space-between",
                marginTop: "-3vh",
              }}
            >
              {quiz.questionsArray[currentQuestionIndex].optionValues.map(
                (option, index) => (
                  <div
                    onClick={() => handleOptionSelect(index)}
                    style={{
                      border:
                        selectedOptionIndex === index
                          ? "4px solid #5076FF"
                          : "",
                      backgroundColor: "#F0F0F0",
                      paddingTop: "3.5vh",
                      paddingBottom: "3.5vh",
                      width: "20vw",
                      marginTop: "10vh",
                      fontWeight: "600",
                      fontSize: "1.6em",
                      textAlign: "center",
                      cursor: "pointer",
                      borderRadius: "10px",
                    }}
                  >
                    {option.value}
                  </div>
                )
              )}
            </div>

            <div
              style={{
                marginTop: "4vh",
                textAlign: "center",
              }}
            >
              <button
                onClick={() => handleNextOrSubmit()}
                style={{
                  color: "#FFFFFF",
                  backgroundColor: "#60B84B",
                  borderRadius: "10px",
                  border: "none",
                  paddingTop: "2vh",
                  paddingBottom: "2vh",
                  width: "20vw",
                  fontSize: "1.3em",
                  fontWeight: "600",
                  marginTop: "3vh",
                  marginBottom: "2vh",
                  cursor: "pointer",
                }}
              >
                {currentQuestionIndex < quiz.questionsArray.length - 1
                  ? "NEXT"
                  : "SUBMIT"}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default LiveQuizInterface;
