import React, { useState, useEffect } from "react";
import plusicon from "../images/plusicon.png";
import smallcrossicon from "../images/smallcrossicon.png";
import EachQuestion from "./EachQuestion";

function AddQuestionsPopup({
  setIsAddQuestionsPopupOpen,
  setIsQuizPublishedPopupOpen,
}) {
  const [selectedQuestion, setSelectedQuestion] = useState(1);
  const [questions, setQuestions] = useState([1]);
  const [renumberedQuestions,setRenumberedQuestions]=useState();

  useEffect(() => {

    setQuestions((prevQuestions) => {
      renumberedQuestions = prevQuestions.map((_, index) => index + 1);
      return renumberedQuestions;
    });
  
    setSelectedQuestion((prevSelectedQuestion) =>
      renumberedQuestions.length > 0 ? renumberedQuestions[0] : 1
    );
  }, [questions]);
  

  const addQuestion = () => {
    if (questions.length < 5) {
      const newQuestionNumber = questions.length + 1;
      setQuestions([...questions, newQuestionNumber]);
      setSelectedQuestion(newQuestionNumber);
    }
  };

  const handleQuestionClick = (questionNumber) => {
    setSelectedQuestion(questionNumber);
  };

  const handleDeleteQuestion = (questionNumber) => {
    const remainingQuestions = questions.filter((q) => q !== questionNumber);
    setQuestions(remainingQuestions);
  };

  return (
    <div
      style={{
        paddingBottom: "7vh",
        paddingTop: "4vh",
        paddingRight: "2.5vw",
        paddingLeft: "2.5vw",
        marginLeft: "3vw",
        backgroundColor: "white",
        borderRadius: "10px",
        width: "50vw",
      }}
    >
      <div
        style={{
          height: "10vh",
          width: "100%",
          display: "flex",
          marginLeft: "2vw",
        }}
      >
        {questions.map((questionNumber, index) => (
          <div key={questionNumber} style={{ position: "relative" }}>
            <p
              onClick={() => handleQuestionClick(questionNumber)}
              style={{
                marginTop: "1vh",
                fontSize: "1.3em",
                color: "#9F9F9F",
                display: "inline-block",
                borderRadius: "50%",
                boxShadow: "0px 0px 10px 0px #00000026",
                width: "8vh",
                height: "8vh",
                textAlign: "center",
                lineHeight: "8vh",
                fontWeight: "600",
                marginRight: "1.5vw",
                cursor: "pointer",
                border:
                  selectedQuestion === questionNumber
                    ? "2px solid #60B84B"
                    : "",
              }}
            >
              {questionNumber}
              {index !== 0 && (
                <img
                  onClick={() => handleDeleteQuestion(questionNumber)}
                  src={smallcrossicon}
                  alt="smallcrossicon"
                  style={{
                    position: "absolute",
                    marginLeft: "0.5vw",
                    marginTop: "0.5vh",
                    cursor: "pointer",
                  }}
                />
              )}
            </p>
            {index === questions.length - 1 && (
              <span>
                <img
                  style={{ cursor: "pointer" }}
                  onClick={addQuestion}
                  src={plusicon}
                  alt="plusicon"
                />
              </span>
            )}
          </div>
        ))}

        <div style={{ textAlign: "right", flex: "1" }}>
          <p
            style={{
              color: "#9F9F9F",
              fontSize: "1.2em",
              lineHeight: "9vh",
              marginRight: "3vw",
              textAlign: "right",
            }}
          >
            Max 5 questions
          </p>
        </div>
      </div>

      <EachQuestion
        key={selectedQuestion}
        setIsAddQuestionsPopupOpen={setIsAddQuestionsPopupOpen}
        setIsQuizPublishedPopupOpen={setIsQuizPublishedPopupOpen}
      />
    </div>
  );
}

export default AddQuestionsPopup;
