import React, { useState, useEffect } from "react";
import deleteicon from "../images/deleteIcon.png";
import styles from "./OptionInputs.module.css";

function OptionInputsText({
  questionsArray,
  onQuestionChange,
  quizType,
  editableQuiz,
}) {
  const [options, setOptions] = useState([]);
  const [selectedOptionId, setSelectedOptionId] = useState(null);

  useEffect(() => {
    if (editableQuiz) {
      const updatedOptions = questionsArray.optionValues.map(
        (option, index) => ({
          ...option,
          id: `${index + 1}`,
        })
      );
      setOptions(updatedOptions);
    }
    // eslint-disable-next-line
  }, [editableQuiz]);

  const handleAddOption = () => {
    setOptions((prevOptions) => {
      if (prevOptions.length < 4) {
        const newOption = {
          id: prevOptions.length + 1,
          value: "",
        };
        const updatedOptions = [...prevOptions, newOption];

        const updatedQuestion = {
          ...questionsArray,
          optionValues: updatedOptions.map((option) => ({
            value: option.value,
            imageUrl: option.imageUrl,
          })),
        };

        onQuestionChange(updatedQuestion);

        return updatedOptions;
      }

      return prevOptions;
    });
  };

  const handleDeleteOption = (id) => {
    if (options.length > 2 && id > 2) {
      setOptions((prevOptions) => {
        const updatedOptions = prevOptions.filter((option) => option.id !== id);

        const updatedQuestion = {
          ...questionsArray,
          optionValues: updatedOptions.map((option) => ({
            value: option.value,
            imageUrl: option.imageUrl,
          })),
        };

        onQuestionChange(updatedQuestion);

        return updatedOptions;
      });
    }
  };

  const handleInputChange = (id, value) => {
    setOptions((prevOptions) => {
      const updatedOptions = prevOptions.map((option) =>
        option.id === id ? { ...option, value } : option
      );
      if (updatedOptions.length < 4 && id === updatedOptions.length) {
        const newOption = {
          id: updatedOptions.length + 1,
          value: "",
        };
        updatedOptions.push(newOption);
      }

      const updatedQuestion = {
        ...questionsArray,
        optionValues: updatedOptions.map((option) => ({
          value: option.value,
          imageUrl: option.imageUrl,
        })),
      };

      onQuestionChange(updatedQuestion);

      return updatedOptions;
    });
  };

  const handleRadioChange = (id) => {
    setOptions((prevOptions) =>
      prevOptions.map((option) =>
        option.id === id ? { ...option, type: "textOptionType" } : option
      )
    );
    setSelectedOptionId(id);

    const questionIndex = id - 1;

    onQuestionChange({
      ...questionsArray,
      correctAnswer: questionIndex,
    });
  };

  return (
    <div>
      {options.map((option) => (
        <div key={option.id} style={{ display: "flex", alignItems: "center" }}>
          {quizType === "QnA" && editableQuiz === null && (
            <input
              type="radio"
              name="OptionsGroup"
              id={`option${option.id}`}
              onChange={() => handleRadioChange(option.id)}
            />
          )}
          <label htmlFor={`option${option.id}`}>
            <input
              style={{
                backgroundColor:
                  option.id === selectedOptionId ? "#60B84B" : "white",
                color: option.id === selectedOptionId ? "white" : "black",
              }}
              className={styles.text1}
              type="text"
              placeholder="Text"
              value={option.value}
              onChange={(e) => handleInputChange(option.id, e.target.value)}
            />
          </label>
          {option.id > 2 && (
            <img
              style={{  }}
              src={deleteicon}
              alt="deleteicon"
              onClick={() => handleDeleteOption(option.id)}
            />
          )}
        </div>
      ))}

      {options.length < 4 && (
        <div>
          <button
            style={{
              color: "#9F9F9F",
              paddingTop: "1vh",
              paddingBottom: "1vh",
              width: "16vw",
              boxShadow: "0px 0px 25px 0px #00000026",
              borderRadius: "10px",
              border: "none",
              background: "none",
              fontSize: "1.2em",
              marginLeft: "2vw",
              marginTop: "5vh",
            }}
            onClick={handleAddOption}
          >
            Add option
          </button>
        </div>
      )}
    </div>
  );
}
export default OptionInputsText;
