import React, { useState, useEffect } from "react";
import plusicon from "../images/plusicon.png";
import smallcrossicon from "../images/smallcrossicon.png";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import EditEachQuestion from "./EditEachQuestion";

function EditQuestionsPopup({ editableQuiz, setIsEditQuestionsPopupOpen }) {
  const [selectedQuestion, setSelectedQuestion] = useState(1);

  const [questions, setQuestions] = useState([1]);

  const [error, setError] = useState("");

  const [questionsArray, setQuestionsArray] = useState([
    {
      questionNo: 1,
      question: "",
      optionType: "",
      optionValues: [],
      correctAnswer: "",
    },
  ]);

  const [timer, setTimer] = useState(null);

  const handleTimerChange = (newTimer) => {
    setTimer(newTimer, () => {
      setNewQuiz((prevNewQuiz) => ({
        ...prevNewQuiz,
        timer: newTimer,
      }));
    });
  };

  const [newQuiz, setNewQuiz] = useState({
    name: editableQuiz.name,
    type: editableQuiz.type,
    timer: timer,
    impressions: 2,
    questionsArray,
  });

  const addQuestion = () => {
    const prevQuestion = questionsArray[questions.length - 1];
    if (!prevQuestion.question) {
      setError("Please enter the question");
      return;
    }

    if (!prevQuestion.optionType) {
      setError("Please select the option type");
      return;
    }

    if (prevQuestion.optionValues.length === 0) {
      setError("Please enter the options");
      return;
    }

    if (questions.length < 5) {
      const newQuestionNumber = questions.length + 1;

      const newQuestion = {
        questionNo: newQuestionNumber,
        question: "",
        optionType: "",
        optionValues: [],
        correctAnswer: "",
      };

      setQuestions([...questions, newQuestionNumber]);

      setQuestionsArray([...questionsArray, newQuestion]);

      setSelectedQuestion(newQuestionNumber);

      setNewQuiz((prevNewQuiz) => ({
        ...prevNewQuiz,
        questionsArray: [...prevNewQuiz.questionsArray, newQuestion],
      }));
    }
  };

  const handleQuestionClick = (questionNumber) => {
    setSelectedQuestion(questionNumber);
  };

  const handleDeleteQuestion = (questionNumber) => {
    const remainingQuestions = questions.filter((q) => q !== questionNumber);

    const renumberedQuestions = remainingQuestions.map((_, index) => index + 1);

    const updatedQuestionsArray = questionsArray.filter(
      (q) => q.questionNo !== questionNumber
    );

    setQuestions(renumberedQuestions);
    setQuestionsArray(updatedQuestionsArray);

    setNewQuiz((prevNewQuiz) => ({
      ...prevNewQuiz,
      questionsArray: updatedQuestionsArray,
    }));
  };

  const handleCancelBtn = () => {
    setIsEditQuestionsPopupOpen(false);
  };

  useEffect(() => {
    setNewQuiz((prevNewQuiz) => ({
      ...prevNewQuiz,
      timer: timer,
    }));
  }, [timer]);

  const handleQuestionChange = (questionNumber, newQuestion) => {
    setQuestionsArray((prevQuestionsArray) => {
      const updatedQuestionsArray = [...prevQuestionsArray];
      const questionIndex = questionNumber - 1;
      updatedQuestionsArray[questionIndex] = newQuestion;
      return updatedQuestionsArray;
    });

    setNewQuiz((prevNewQuiz) => {
      const updatedQuestionsArray = [...prevNewQuiz.questionsArray];
      const questionIndex = questionNumber - 1;
      updatedQuestionsArray[questionIndex] = newQuestion;

      return {
        ...prevNewQuiz,
        questionsArray: updatedQuestionsArray,
      };
    });
  };

  const handleUpdateQuizBtn = () => {
    toast.success("Quiz Updated Successfully");
    setIsEditQuestionsPopupOpen(false);
  };

  return (
    <div
      style={{
        paddingBottom: "7vh",
        paddingTop: "4vh",
        paddingRight: "2.5vw",
        paddingLeft: "2.5vw",
        marginLeft: "3vw",
        backgroundColor: "white",
        borderRadius: "10px",
        width: "50vw",
      }}
    >
      <div
        style={{
          height: "10vh",
          width: "100%",
          display: "flex",
          marginLeft: "2vw",
        }}
      >
        {questionsArray.map((questionNumber, index) => (
          <div key={questionNumber} style={{ position: "relative" }}>
            <p
              onClick={() => handleQuestionClick(questionNumber)}
              style={{
                marginTop: "1vh",
                fontSize: "1.3em",
                color: "#9F9F9F",
                display: "inline-block",
                borderRadius: "50%",
                boxShadow: "0px 0px 10px 0px #00000026",
                width: "8vh",
                height: "8vh",
                textAlign: "center",
                lineHeight: "8vh",
                fontWeight: "600",
                marginRight: "1.5vw",
                cursor: "pointer",
                border:
                  selectedQuestion === questionNumber
                    ? "2px solid #60B84B"
                    : "",
              }}
            >
              {index + 1}
              {index !== 0 && (
                <img  onClick={() => handleDeleteQuestion(questionNumber)}
                  src={smallcrossicon}
                  alt="smallcrossicon"
                  style={{
                    position: "absolute",
                    marginLeft: "0.5vw",
                    marginTop: "0.5vh",
                    cursor: "pointer",
                  }}
                />
              )}
            </p>
            {index === editableQuiz.questionsArray.length - 1 && (
              <span>
                <img
                  style={{ cursor: "pointer" }}
                  onClick={addQuestion}
                  src={plusicon}
                  alt="plusicon"
                />
              </span>
            )}
          </div>
        ))}

        <div style={{ textAlign: "right", flex: "1" }}>
          <p
            style={{
              color: "#9F9F9F",
              fontSize: "1.2em",
              lineHeight: "9vh",
              marginRight: "3vw",
              textAlign: "right",
            }}
          >
            Max 5 questions
          </p>
        </div>
      </div>

      <EditEachQuestion
        questionNumber={1}
        selected={selectedQuestion === 1}
        editableQuiz={editableQuiz}
        onQuestionChange={(newQuestion) =>
          handleQuestionChange(1, newQuestion)
        }
      />

      {questionsArray.map((questionNumber) => (
        <div key={questionNumber}>
          <EditEachQuestion
            questionNumber={questionNumber}
            selected={selectedQuestion === questionNumber}
            editableQuiz={editableQuiz}
            onQuestionChange={(newQuestion) =>
              handleQuestionChange(questionNumber, newQuestion)
            }
          />
        </div>
      ))}

      <div
        style={{
          height: "6.5vh",
          width: "46vw",
          display: "flex",
          justifyContent: "space-between",
          marginTop: "4vh",
          marginLeft: "2vw",
          marginRight: "2vw",
        }}
      >
        <button
          onClick={handleCancelBtn}
          style={{
            color: "#474444",
            boxShadow: "0px 0px 15px 0px #00000040",
            background: "none",
            fontWeight: "600",
            fontSize: "1.2em",
            width: "17vw",
            borderRadius: "10px",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            border: "none",
            cursor: "pointer",
          }}
        >
          Cancel
        </button>
        <button
          onClick={handleUpdateQuizBtn}
          style={{
            color: "#FFFFFF",
            backgroundColor: "#60B84B",
            fontWeight: "600",
            fontSize: "1.2em",
            width: "17vw",
            borderRadius: "10px",
            paddingTop: "1vh",
            paddingBottom: "1vh",
            border: "none",
            cursor: "pointer",
          }}
        >
          Update Quiz
        </button>
      </div>
    </div>
  );
}

export default EditQuestionsPopup;
