import React from "react";
import axios from "axios";

function DeleteQuizPopup({ quizId, onClose}) {
  const handleDeleteQuiz = () => {
    axios
      .delete("http://localhost:4000/quizDeletion", { data: { quizId } })
      .then((response) => {
        onClose();
      })
      .catch((error) => {
        console.log("Error while deleting  quiz:", error);
      });
  };

  const handleCancelBtn = () => {
    onClose();
  };

  return (
    <div
      style={{
        paddingBottom: "5vh",
        width: "57vw",
        marginTop: "30vh",
        marginLeft: "3vw",
        backgroundColor: "white",
        borderRadius: "10px",
      }}
    >
      <p
        style={{
          fontSize: "2.5em",
          fontWeight: "600",
          color: "#474444",
          textAlign: "center",
          paddingTop: "2vh",
        }}
      >
        <p>Are you confirm you</p>
        <p> want to delete ?</p>
      </p>

      <div
        style={{
          display: "flex",
          marginLeft: "6vw",
          marginTop: "8vh",
          gap: "5vw",
          marginRight: "3vw",
        }}
      >
        <button
          onClick={handleDeleteQuiz}
          style={{
            height: "6vh",
            width: "20vw",
            borderRadius: "10px",
            color: "#FFFFFF",
            fontSize: "1.2em",
            backgroundColor: "#FF4B4B",
            border: "none",
          }}
        >
          Confirm Delete
        </button>
        <button
          onClick={handleCancelBtn}
          style={{
            height: "6vh",
            width: "20vw",
            borderRadius: "10px",
            color: "#474444",
            fontSize: "1.2em",
            backgroundColor: "white",
            boxShadow: "0px 0px 15px 0px #00000040",
            border: "none",
            cursor:"pointer"
          }}
        >
          Cancel
        </button>
      </div>
    </div>
  );
}

export default DeleteQuizPopup;
