import React, { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import LoginDetails from "./LoginDetails";

function SignUpDetails() {
  const [signUpData, setSignUpData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const handleInputChange = (field, value) => {
    setSignUpData({
      ...signUpData,
      [field]: value,
    });
  };

  const [isSignUpClicked, setIsSignUpClicked] = useState(false);
  const [error, setError] = useState("");
  const [showLogin, setShowLogin] = useState(false); 

  const handleSignUp = () => {
    setIsSignUpClicked(true);
    axios
      .post("http://localhost:4000/signup", signUpData)
      .then((response) => {
        const { message } = response.data;
        setError("");
        setSignUpData({
          name: "",
          email: "",
          password: "",
          confirmPassword: "",
        });
        toast.success(message);
        setIsSignUpClicked(false);
      })
      .catch((error) => {
        if (
          error.response &&
          error.response.data &&
          error.response.data.error
        ) {
          setError(error.response.data.error);
        } else {
          setError("An error occurred while creating the user");
        }
        console.log(error);
      });
  };

  return (
    <div style={{ height: "100%", width: "100%" }}>
      <div style={{ display: "flex", height: "100%", width: "100%" }}>
        <div
          style={{
            height: "100%",
            width: "40%",
            textAlign: "right",
            paddingRight: "5vw",
            paddingLeft: "1vw",
            fontWeight: "600",
            color: "#474444",
            fontSize: "1.3em",
            paddingBottom: "3vw",
          }}
        >
          <p style={{ height: "22%", marginTop: "3vh" }}>Name</p>
          <p style={{ height: "22%" }}>Email</p>
          <p style={{ height: "22%" }}>Password</p>
          <p style={{ height: "22%" }}>Confirm Password</p>
        </div>
        <div style={{ height: "100%", width: "60%" }}>
          <form
            method="post"
            style={{ marginTop: "-1vh", paddingBottom: "3vw" }}
          >
            <p>
              <input
                style={{
                  border:
                    isSignUpClicked && signUpData.name.length === 0
                      ? "1px solid #D60000"
                      : "",
                }}
                className="signup-login-input"
                value={signUpData.name}
                onChange={(e) => handleInputChange("name", e.target.value)}
                placeholder={
                  isSignUpClicked && signUpData.name.length === 0
                    ? "Invalid name"
                    : ""
                }
              ></input>
            </p>
            <p>
              <input
                style={{
                  border:
                    isSignUpClicked && signUpData.email.length === 0
                      ? "1px solid #D60000"
                      : "",
                }}
                className="signup-login-input"
                value={signUpData.email}
                onChange={(e) => handleInputChange("email", e.target.value)}
                placeholder={
                  isSignUpClicked && signUpData.email.length === 0
                    ? "Invalid email"
                    : ""
                }
              ></input>
            </p>
            <p>
              <input
                style={{
                  border:
                    isSignUpClicked && signUpData.password.length === 0
                      ? "1px solid #D60000"
                      : "",
                }}
                className="signup-login-input"
                value={signUpData.password}
                onChange={(e) => handleInputChange("password", e.target.value)}
                placeholder={
                  isSignUpClicked && signUpData.password.length === 0
                    ? "Invalid password"
                    : ""
                }
              ></input>
            </p>
            <p>
              <input
                style={{
                  border:
                    isSignUpClicked &&
                    (signUpData.confirmPassword.length === 0 ||
                    signUpData.confirmPassword !== signUpData.password)
                      ? "1px solid #D60000"
                      : "",
                }}
                className="signup-login-input"
                value={signUpData.confirmPassword}
                onChange={(e) =>
                  handleInputChange("confirmPassword", e.target.value)
                }
                placeholder={
                  isSignUpClicked && signUpData.confirmPassword.length === 0
                    ? "Password doesn't match"
                    : ""
                }
              ></input>
            </p>
          </form>
        </div>
      </div>
      <div
        style={{
          height: "15%",
          width: "100%",
          marginBottom: "2vh",
          marginTop: "1vh",
        }}
      >
        <button
          onClick={handleSignUp}
          style={{
            marginLeft: "35vw",
            marginRight: "35vw",
            backgroundColor: "#A9BCFF",
            height: "90%",
            width: "22%",
            borderRadius: "8px",
            border: "none",
            fontWeight: "600",
            fontSize: "1.3em",
            color: "#474444",
          }}
        >
          Sign-Up
        </button>
      </div>
      {error ? (
        <div>
          <p
            style={{
              color: "#D60000",
              fontSize: "1.5rem",
              marginTop: "5vh",
              textAlign: "center",
              marginBottom: "2vh",
            }}
          >
            {error}
          </p>
        </div>
      ) : (
        <></>
      )}
    </div>
  );
}

export default SignUpDetails;
