import React, { useEffect, useState } from "react";
import Timer from "./Timer";
import OptionInputsText from "./OptionInputsText";
import OptionInputsImage from "./OptionInputsImage";
import OptionInputsTextAndImage from "./OptionInputsTextAndImage";

function EachQuestion({
  questionNumber,
  selected,
  questionsArray,
  onQuestionChange,
  quizType,
  onTimerChange
}) {
  const [selectedOptionType, setSelectedOptionType] = useState("");

  const [questionValue, setQuestionValue] = useState("");


  const handleQuestionChange = (e) => {
    const updatedQuestion = {
      ...questionsArray,
      question: e.target.value,
    };

    onQuestionChange(updatedQuestion);
    setQuestionValue(e.target.value);
  };

  const handleSelectedOption = (value) => {
    const updatedQuestion = {
      ...questionsArray,
      optionType: value,
    };
    onQuestionChange(updatedQuestion);
    setSelectedOptionType(value);
  };

  const renderOptionInputs = () => {
    switch (selectedOptionType) {
      case "Text":
        return (
          <OptionInputsText
            questionsArray={questionsArray}
            onQuestionChange={onQuestionChange}
            quizType={quizType}
          />
        );
      case "ImageUrl":
        return (
          <OptionInputsImage
            questionsArray={questionsArray}
            onQuestionChange={onQuestionChange}
            quizType={quizType}
          />
        );
      case "TextAndImageUrl":
        return (
          <OptionInputsTextAndImage
            questionsArray={questionsArray}
            onQuestionChange={onQuestionChange}
            quizType={quizType}
          />
        );
      default:
        return null;
    }
  };

  useEffect(() => {
    console.log(timer);
  }, [timer]);

  return (
    <div style={{ display: selected ? "block" : "none" }}>
      <div style={{ height: "8vh", width: "50vw" }}>
        <input
          style={{
            width: "46.5vw",
            marginLeft: "2vw",
            marginTop: "3vh",
            marginRight: "2vw",
            paddingTop: "1.5vh",
            paddingBottom: "1.5vh",
            paddingLeft: "1vw",
            border: "none",
            fontSize: "1.2em",
            borderRadius: "10px",
            boxShadow: "0px 0px 25px 0px #00000026",
          }}
          type="text"
          value={questionValue}
          onChange={handleQuestionChange}
          placeholder={`Question ${questionNumber}`}
        ></input>
      </div>

      <div
        style={{
          marginTop: "4.5vh",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          height: "6vh",
          width: "46vw",
          marginLeft: "2.5vw",
          marginRight: "2.5vw",
          color: "#9F9F9F",
          fontSize: "1.2em",
        }}
      >
        <div style={{ marginRight: "1vw" }}>
          <p>Option Type </p>
        </div>

        <div style={{ marginRight: "1vw" }}>
          <input
            onClick={() => {
              handleSelectedOption("Text");
            }}
            type="radio"
            name={`optionType-${questionNumber}`}
            id={`Text-${questionNumber}`}
          />
          <label htmlFor={`Text-${questionNumber}`}> Text</label>
        </div>

        <div style={{ marginRight: "1vw" }}>
          <input
            onClick={() => {
              handleSelectedOption("ImageUrl");
            }}
            type="radio"
            name={`optionType-${questionNumber}`}
            id={`ImageUrl-${questionNumber}`}
          />

          <label htmlFor={`ImageUrl-${questionNumber}`}> Image URL</label>
        </div>

        <div>
          <input
            onClick={() => {
              handleSelectedOption("TextAndImageUrl");
            }}
            type="radio"
            name={`optionType-${questionNumber}`}
            id={`TextAndImageUrl-${questionNumber}`}
          />
          <label htmlFor={`TextAndImageUrl-${questionNumber}`}>
            {" "}
            Text & Image URL
          </label>
        </div>
      </div>

      <div
        style={{
          height: "38vh",
          width: "48vw",
          display: "flex",
          marginLeft: "1vw",
          marginRight: "1vw",
          justifyContent: "space-between",
          marginTop: "3vh",
          overflow: "auto",
          marginBottom: "4vh",
        }}
      >
        <div style={{ width: "35vw" }}>{renderOptionInputs()}</div>

        {quizType === "QnA" && (
          <div style={{ width: "10vw" }}>
            <Timer onTimerChange={onTimerChange} />
          </div>
        )}
      </div>
    </div>
  );
}

export default EachQuestion;
