import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import OptionBox from "./OptionBox";

function LiveQuizInterface() {
  const { quizId } = useParams();

  const [quiz, setQuiz] = useState();

  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  useEffect(() => {
    axios
      .get(`http://localhost:4000/getQuizById/${quizId}`)
      .then((response) => {
        const { quiz } = response.data;
        setQuiz(quiz);
      })
      .catch((error) => {
        console.log(error);
      });
  });

  const handleNextOrSubmit = () => {
    if (currentQuestionIndex < quiz.questionsArray.length - 1) {
      setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
    } else {
    
      alert("Quiz Submitted!");
    }
  };

  return (
    <div
      style={{
        height: "100%",
        width: "100%",
        backgroundColor: "#041325",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <div
        style={{
          height: "82vh",
          width: "90vw",
          backgroundColor: "#FFFFFF",
          marginLeft: "5vw",
          marginRight: "5vw",
          marginTop: "9vh",
          marginBottom: "9vh",
          borderRadius: "15px",
          boxShadow: "0px 0px 30px 0px #00000026",
        }}
      >
        {quiz && quiz.questionsArray && quiz.questionsArray.length > 0 && (
          <div>
            {/* ... (previous code) */}

            <div style={{ marginTop: "6vh", textAlign: "center" }}>
              <button
                onClick={handleNextOrSubmit}
                style={{
                  color: "#FFFFFF",
                  backgroundColor: "#60B84B",
                  borderRadius: "10px",
                  border: "none",
                  paddingTop: "2vh",
                  paddingBottom: "2vh",
                  width: "17vw",
                  fontSize: "1.3em",
                  fontWeight: "600",
                }}
              >
                {currentQuestionIndex < quiz.questionsArray.length - 1
                  ? "NEXT"
                  : "SUBMIT"}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default LiveQuizInterface;
